import { doc, getDoc } from "firebase/firestore";
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { db } from "../fireba/firebase";
import photo from "./profile-user.png"
import change from "./cigarette.png"
import setting from "./settings.png"
import multipe from "./multiple-users-silhouette.png"
import friend from "./friends.png"
function Setting() {
  const { currentUser } = useAuth(); 
  const [userData, setUserData] = useState({ name: "", age: "", country: "" });
  const [error, setError] = useState("");
  const { logout } = useAuth();
  const navigate = useNavigate();
  const emailRef = useRef();
  const passwordRef = useRef();
  const passwordConfirmRef = useRef();
  const { updateUserPassword, updateUserEmail } = useAuth();
  const [loading, setLoading] = useState(false);
  

  useEffect(() => {
    async function loadUserData() {
      const data = await fetchUserData(currentUser.uid); // Replace with dynamic ID
      if (data) {
        setUserData(data);
      }
    }
    loadUserData();
  }, []);

  async function fetchUserData(userId) {
    const docRef = doc(db, "users", userId);
    const docSnap = await getDoc(docRef);

    if (docSnap.exists()) {
      return docSnap.data();
    } else {
      console.error("No such document!");
      return null;
    }
  }
  function handleSubmit(e) {
  const promises = [];
  setLoading(true);
  setError("");
  if (passwordRef.current.value) {
    promises.push(updateUserPassword(passwordRef.current.value));
  }

  Promise.all(promises)
    .then(() => {
      navigate("/Setting");
    })
    .catch(() => {
      setError("changepassword");
    })
    .finally(() => {
      setLoading(false);
    });
}

    return(
        <div class="page d-flex">
        <div class="sidebar bg-white p-20 p-relative">
          <h3 class="p-relative txt-c mt-0">habit-up</h3>
          <ul>
      <li>
      <Link to="/dashboard" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={photo} style={{ width: '24px' }}/><span>Dashboard</span></Link>
          </li>
          <li>
          <Link to="/setting" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={setting} style={{ width: '24px' }}/><span>Setting</span></Link>
          </li>
          <li>
          <Link to="/yourhabits" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={change} style={{ width: '24px' }}/><span>habit</span></Link>
          </li>
          <li>
          <Link to="/challenges" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={multipe} style={{ width: '24px' }}/><span>challenge</span></Link>
          </li>
          <li>
          <Link to="/friends" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={friend} style={{ width: '24px' }}/><span>friends</span></Link>
          </li>
      </ul>
        </div>
        <div class="content w-full">
          <h1 class="p-relative">Settings</h1>
          <div class="settings-page m-20 d-grid gap-20">
            <div class="p-20 bg-white rad-10">
              <h2 class="mt-0 mb-10">Site Control</h2>
              <p class="mt-0 mb-20 c-grey fs-15">Control The Website If There Is Maintenance</p>
              <div class="mb-15 between-flex">
                <div>
                  <span>Website Control</span>
                  <p class="c-grey mt-5 mb-0 fs-13">Open/Close Website And Type The Reason</p>
                </div>
                <div>
                  <label>
                    <input class="toggle-checkbox" type="checkbox" checked />
                    <div class="toggle-switch"></div>
                  </label>
                </div>
              </div>
              <textarea class="close-message p-10 rad-6 d-block w-full" placeholder="Close Message Content"></textarea>
            </div>
            <div class="p-20 bg-white rad-10">
              <h2 class="mt-0 mb-10">General Info</h2>
              <p class="mt-0 mb-20 c-grey fs-15">General Information About Your Account</p>
              <div class="mb-15">
                <label class="fs-14 c-grey d-block mb-10" for="first">First Name</label>
                <input
                  class="b-none border-ccc p-10 rad-6 d-block w-full"
                  type="text"
                  id="first"
                  placeholder="First Name"
                  value={userData.name}
                />
              </div>
              <div>
                <label class="fs-14 c-grey d-block mb-5" for="email">Email</label>
                <input type="email" value={currentUser && currentUser.email} readOnly/>             
                <a class="c-blue" href="#">Change</a>
              </div>
            </div>
            <div class="p-20 bg-white rad-10">
              <h2 class="mt-0 mb-10">Security Info</h2>
              <p class="mt-0 mb-20 c-grey fs-15">Security Information About Your Account</p>
              <div class="sec-box mb-15 between-flex">
                <div>
                  <span>Password</span>
                  <p class="c-grey mt-5 mb-0 fs-13">Last Change On 25/10/2021</p>
                </div>
                <button class="button bg-blue c-white btn-shape" onSubmit={handleSubmit}><pre>Change</pre></button>
              </div>
  
              </div>

            </div>
          </div>
        </div>
    );
}
export default Setting;
