import React, { useState } from 'react';
import './index.scss';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [habits, setHabits] = useState([]);

  // Fonction de gestion de la saisie
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  // Fonction pour ajouter une habitude et vider le champ de saisie
  const handleAddHabit = () => {
    if (inputValue.trim() === '') {
      alert('Please enter a habit before submitting!');
    } else {
      setHabits([...habits, inputValue]); // Ajouter la nouvelle habitude au tableau
      setInputValue(''); // Effacer la valeur de l'input
    }
  };

  return (
    <div className='categories-container'>
      <div className='category-container'>
        <div className='category-body-container'>
          <h2>Add a New Habit</h2>
          <input 
            type="text" 
            placeholder="Type a new habit..." 
            value={inputValue}
            onChange={handleInputChange} 
          />
          <button onClick={handleAddHabit}>Add Habit</button>

          {/* Affichage des habitudes ajout√©es dans des rectangles */}
          <div className="habits-grid">
            {habits.map((habit, index) => (
              <div key={index} className="habit-card">
                <p>{habit}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
