import React, { useState, useEffect } from 'react';

const calendar = () => {
  const [modals, setModals] = useState([]);
  const [overlayActive, setOverlayActive] = useState(false);
  const [today, setToday] = useState(new Date());
  const [activeDay, setActiveDay] = useState(null);
  const [month, setMonth] = useState(today.getMonth());
  const [year, setYear] = useState(today.getFullYear());
  const [eventsArr, setEventsArr] = useState([]);
  const [eventForm, setEventForm] = useState({
    title: '',
    timeFrom: '',
    timeTo: ''
  });
  const [todoList, setTodoList] = useState([]);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
    const savedEvents = JSON.parse(localStorage.getItem('events')) || [];
    setEventsArr(savedEvents);
  }, []);

  useEffect(() => {
    localStorage.setItem('events', JSON.stringify(eventsArr));
  }, [eventsArr]);

  const months = [
    'January', 'February', 'March', 'April', 'May', 'June',
    'July', 'August', 'September', 'October', 'November', 'December'
  ];

  const handleModalOpen = (modalId) => {
    setModals([...modals, modalId]);
    setOverlayActive(true);
  };

  const handleModalClose = () => {
    setModals([]);
    setOverlayActive(false);
  };

  const prevMonth = () => {
    setMonth((prev) => (prev === 0 ? 11 : prev - 1));
    setYear((prev) => (month === 0 ? prev - 1 : prev));
  };

  const nextMonth = () => {
    setMonth((prev) => (prev === 11 ? 0 : prev + 1));
    setYear((prev) => (month === 11 ? prev + 1 : prev));
  };

  const handleDayClick = (day) => {
    setActiveDay(day);
  };

  const handleEventSubmit = () => {
    if (!eventForm.title || !eventForm.timeFrom || !eventForm.timeTo) {
      alert('Please fill all the fields');
      return;
    }

    const newEvent = {
      title: eventForm.title,
      time: `${convertTime(eventForm.timeFrom)} - ${convertTime(eventForm.timeTo)}`
    };

    const newEventsArr = [...eventsArr];
    const dayEvents = newEventsArr.find(
      (event) => event.day === activeDay && event.month === month + 1 && event.year === year
    );

    if (dayEvents) {
      dayEvents.events.push(newEvent);
    } else {
      newEventsArr.push({
        day: activeDay,
        month: month + 1,
        year,
        events: [newEvent]
      });
    }

    setEventsArr(newEventsArr);
    setEventForm({ title: '', timeFrom: '', timeTo: '' });
  };

  const handleDeleteEvent = (eventTitle) => {
    const newEventsArr = eventsArr.map((event) => {
      if (event.day === activeDay && event.month === month + 1 && event.year === year) {
        event.events = event.events.filter((e) => e.title !== eventTitle);
      }
      return event;
    }).filter((event) => event.events.length > 0);

    setEventsArr(newEventsArr);
  };

  const convertTime = (time) => {
    const [hour, minute] = time.split(':');
    const period = hour >= 12 ? 'PM' : 'AM';
    const convertedHour = hour % 12 || 12;
    return `${convertedHour}:${minute} ${period}`;
  };

  const handleTodoAdd = () => {
    if (!eventForm.title) {
      alert('Task cannot be empty');
      return;
    }

    const newTodo = {
      task: eventForm.title,
      id: Date.now().toString(),
      complete: false
    };

    setTodoList([...todoList, newTodo]);
    setEventForm({ ...eventForm, title: '' });
  };

  const handleTodoDelete = (id) => {
    setTodoList(todoList.filter((todo) => todo.id !== id));
  };

  const handleTodoComplete = (id) => {
    const newTodoList = todoList.map((todo) => {
      if (todo.id === id) {
        todo.complete = !todo.complete;
      }
      return todo;
    });

    setTodoList(newTodoList);
  };

  const filteredTodos = todoList.filter((todo) => {
    if (filter === 'all') return true;
    if (filter === 'completed') return todo.complete;
    if (filter === 'remaining') return !todo.complete;
    return true;
  });

  return (
    <div className="App">
      {/* Modal */}
      <div className={`overlay ${overlayActive ? 'active' : ''}`} onClick={handleModalClose}></div>
      {modals.map((modalId) => (
        <div key={modalId} className="modal active">
          <div className="modal-content">
            <button className="close" onClick={handleModalClose}>Close</button>
            {/* Add your modal content here */}
          </div>
        </div>
      ))}

      {/* Calendar */}
      <div className="calendar">
        <div className="header">
          <button className="prev" onClick={prevMonth}>Prev</button>
          <div className="date">{months[month]} {year}</div>
          <button className="next" onClick={nextMonth}>Next</button>
        </div>
        <div className="days">
          {/* Render days here */}
        </div>
      </div>

      {/* Event Form */}
      <div className="event-form">
        <input
          type="text"
          className="event-name"
          value={eventForm.title}
          onChange={(e) => setEventForm({ ...eventForm, title: e.target.value })}
          placeholder="Event Name"
        />
        <input
          type="text"
          className="event-time-from"
          value={eventForm.timeFrom}
          onChange={(e) => setEventForm({ ...eventForm, timeFrom: e.target.value })}
          placeholder="Time From"
        />
        <input
          type="text"
          className="event-time-to"
          value={eventForm.timeTo}
          onChange={(e) => setEventForm({ ...eventForm, timeTo: e.target.value })}
          placeholder="Time To"
        />
        <button className="add-event-btn" onClick={handleEventSubmit}>Add Event</button>
      </div>

      {/* Todo List */}
      <div className="todo-list">
        <input
          type="text"
          className="todo-input"
          value={eventForm.title}
          onChange={(e) => setEventForm({ ...eventForm, title: e.target.value })}
          placeholder="Add a task"
        />
        <button className="add-button" onClick={handleTodoAdd}>Add Task</button>
        <button className="delete-all" onClick={() => setTodoList([])}>Delete All</button>
        <button className="delete-selected" onClick={() => setTodoList(todoList.filter((todo) => !todo.complete))}>Delete Completed</button>
        <ul className="all-todos">
          {filteredTodos.map((todo) => (
            <li key={todo.id} className="todo-item">
              <p className={todo.complete ? 'complete' : ''}>{todo.task}</p>
              <button className="complete" onClick={() => handleTodoComplete(todo.id)}>Done</button>
              <button className="delete" onClick={() => handleTodoDelete(todo.id)}>Del</button>
            </li>
          ))}
        </ul>
        <div className="filters">
          <button id="all" onClick={() => setFilter('all')}>All</button>
          <button id="rem" onClick={() => setFilter('remaining')}>Remaining</button>
          <button id="com" onClick={() => setFilter('completed')}>Completed</button>
        </div>
      </div>
    </div>
  );
};

export default calendar;
