import React, { useState, useMemo, useRef } from "react";
import { ref, push, set } from "firebase/database";
import Select from 'react-select';
import logo1 from "./habit-tracker-gif-looponce.gif";
import countryList from 'react-select-country-list';
import { Alert } from "react-bootstrap";
import { useNavigate, Link} from "react-router-dom";
import { db } from "../fireba/firebase";
import { collection, addDoc } from "firebase/firestore";
import "./register-login.css";
export default function Registre() {
  const [value, setValue] = useState('');
  const options = useMemo(() => countryList().getData(), []);

  const changeHandler = value => {
    setValue(value);
  }

  const nameRef = useRef();
  const countryRef = useRef();
  const ageref = useRef();
  const passwordRef = useRef(); // Déclaration de passwordRef
  const passwordConfirmRef = useRef(); // Déclaration de passwordConfirmRef
  const emailRef = useRef(); // Déclaration de emailRef (si nécessaire)

  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [country, setcountry] = useState("");
  const [age, setage] = useState("");
  const [loader, setLoader] = useState(false);
  const [alert, setAlert] = useState(false);
  const [showPassword, setShowPassword] = useState(false); // Déclaration de showPassword
  const [showPasswordConfirm, setShowPasswordConfirm] = useState(false); // Déclaration de showPasswordConfirm

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoader(true);
  
    // Vérification des erreurs dans les champs
    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      setError("Passwords do not match");
      setLoader(false);
      return;
    }
    if (!nameRef.current.value) {
      setError("Please enter a name");
      setLoader(false);
      return;
    }
    if (!value) {
      setError("Please select a country");
      setLoader(false);
      return;
    }
    if (!ageref.current.value) {
      setError("Please enter your age");
      setLoader(false);
      return;
    }
  
    // Sauvegarder les données dans Firestore
    try {
      setError(""); // Réinitialisation des erreurs
  
      await addDoc(collection(firestoreDb, "users"), {
        name: nameRef.current.value,
        country: value.label,
        age: ageref.current.value,
        email: emailRef.current.value,
      });
  
      setLoader(false);
      setAlert(true);
  
      // Réinitialisation des champs
      setName("");
      setcountry("");
      setage("");
  
      // Masquer l'alerte après 3 secondes
      setTimeout(() => setAlert(false), 3000);
  
      // Rediriger après l'enregistrement
      navigate("/Dashboard", {
        state: { name: nameRef.current.value, country: value.label, age: ageref.current.value },
      });
    } catch (error) {
      setError("Failed to save data to Firestore");
      console.error("Firestore Error:", error);
    } finally {
      setLoader(false);
    }
  };
  return (
    <div className="loginmainContainer">
      <div className="loginmainBody">
        <img className="logo" src={logo1} alt="Habit tracker gif" />
        <h1>Register new account</h1>
        {error && <Alert variant="danger">{error}</Alert>}
        <form id="registerForm" onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" required ref={emailRef} />
          <label htmlFor="password">Password:</label>
          <p>
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              id="password"
              required
              ref={passwordRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword1"
              style={{ marginLeft: "-30px", cursor: "pointer" }}
              onClick={() => setShowPassword((prev) => !prev)}
            ></i>
          </p>

          <label htmlFor="confirmPassword">Confirm Password:</label>
          <p>
            <input
              type={showPasswordConfirm ? "text" : "password"}
              name="confirmPassword"
              id="confirmPassword"
              required
              ref={passwordConfirmRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword2"
              style={{ marginLeft: "-30px", cursor: "pointer" }}
              onClick={() => setShowPasswordConfirm((prev) => !prev)}
            ></i>
          </p>

          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" required ref={nameRef} />
          <label htmlFor="country">Country</label>
          <Select options={options} value={value} onChange={changeHandler}ref={countryRef} />
          <label htmlFor="age">Age:</label>
          <input type="number" id="age" name="age" required ref={ageref} />
          <input disabled={loader} type="submit" id="registerButton" name="registerButton" value="Complete" />
        </form>
        <p className="loginlink">
          Already have an account?<Link to="/login"> login here</Link>
        </p>
      </div>
    </div>
  );
}
