import React, { useRef, useState, useMemo } from "react";
import {
   ref, push, set } from "../fireba/firebase";
import Select from 'react-select';
import countryList from 'react-select-country-list';
import { Alert } from "react-bootstrap";
import "./register-login.css";
import { useAuth } from "../context/AuthContext";
import { Link, useNavigate } from "react-router-dom";
import logo1 from "./habit-tracker-gif-looponce.gif";
import "./register-login.css";
import { db ,auth} from "../fireba/firebase"

export default function Registre() {
  const [value, setValue] = useState('');
  const options = useMemo(() => countryList().getData(), []);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState(false);  // Pour afficher une alerte après l'enregistrement
  const navigate = useNavigate();
  
  const nameRef = useRef();
  const ageRef = useRef();
  const emailRef = useRef();
  const passwordRef = useRef();
  const passwordConfirmRef = useRef();
  const { signup } = useAuth();
  
  const [showPassword, setShowPassword] = useState(false);
  const [showPasswordConfirm, setShowPasswordConfirm] = useState(false);

  const changeHandler = value => {
    setValue(value);
  };

  async function handleSubmit(e) {
    e.preventDefault();
    
    // Vérification des champs
    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError("Passwords do not match");
    }
    if (nameRef.current.value === "") {
      return setError("Please enter a name");
    }
    if (!value) {
      return setError("Please select a country");
    }
    if (ageRef.current.value === "") {
      return setError("Please enter your age");
    }

    try {
      setError("");
      setLoading(true);
      
      // Inscription de l'utilisateur
      await signup(emailRef.current.value, passwordRef.current.value);

      const currentUser = auth.currentUser;
      const name = nameRef.current.value;
      const country = value.label;
      const age = ageRef.current.value;

      // Sauvegarder les données utilisateur dans Firebase Realtime Database
      const database = getDatabase();
      const messagesRef = ref(database, 'compte');
      const newMessageRef = push(messagesRef);
      
      // Enregistrement des données
      await set(newMessageRef, {
        name: name,
        country: country,
        age: age,
      });

      // Une fois les données enregistrées avec succès, affichage de l'alerte et navigation vers le tableau de bord
      setAlert(true);
      setTimeout(() => setAlert(false), 3000); // Ferme l'alerte après 3 secondes
      navigate("/Dashboard", { state: { name, country, age } });

    } catch (error) {
      setError("Failed to create an account");
      console.error(error);
      setError(error.message || "Failed to create an account");
    } finally {
      setLoading(false);
    }}
  return (
    <div className="loginmainContainer">
      <div className="loginmainBody">
        <img className="logo" src={logo1} alt="Habit tracker gif" />
        <h1>Register new account</h1>
        {error && <Alert variant="danger">{error}</Alert>}
        <form id="registerForm" onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" required ref={emailRef} />
          <label htmlFor="password">Password:</label>
          <p>
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              id="password"
              required
              ref={passwordRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword1"
              style={{ marginLeft: "-30px", cursor: "pointer" }}
              onClick={() => setShowPassword((prev) => !prev)}
            ></i>
          </p>
          
          <label htmlFor="confirmPassword">Confirm Password:</label>
          <p>
            <input
              type={showPasswordConfirm ? "text" : "password"}
              name="confirmPassword"
              id="confirmPassword"
              required
              ref={passwordConfirmRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword2"
              style={{ marginLeft: "-30px", cursor: "pointer" }}
              onClick={() => setShowPasswordConfirm((prev) => !prev)}
            ></i>
          </p>
          
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" required ref={nameRef} />
          <label htmlFor="country">Country</label>
          <Select options={options} value={value} onChange={changeHandler} />
          <label htmlFor="age">Age:</label>
          <input type="number" id="age" name="age" required ref={ageRef} />
          <input disabled={loading} type="submit" id="registerButton" name="registerButton" value="Complete" />
        </form>
        <p className="loginlink">
          Already have an account?<Link to="/login"> login here</Link>
        </p>
      </div>
    </div>
  );
}
