import React, { useState, useEffect } from "react";
import { doc, getDoc, setDoc, updateDoc } from "firebase/firestore";
import { useAuth } from "../context/AuthContext";
import { db } from "../fireba/firebase";

const Calendrier = () => {
  const [month, setMonth] = useState([]); 
  const [newMonth, setNewMonth] = useState({ target: ""});
  const [error, setError] = useState("");
  const { currentUser } = useAuth();
  useEffect(() => {
    async function loadMonthData() {
      try {
        const monthRef = doc(db, "month", currentUser.uid);
        const monthSnap = await getDoc(monthRef);

        if (monthSnap.exists()) {
          setMonth(monthSnap.data().month || []);
        }
      } catch (err) {
        console.error("Erreur lors du chargement des donn√©es :", err);
      }
    }
    loadMonthData();
  }, [currentUser.uid]);

  const handleAddmounth = async () => {
    if (!newMonth.target()) {
      alert("Veuillez remplir tous les champs.");
      return;
    }

    const updatedMonth = [
      ...month,
      { id: month.length + 1, target: newMonth.month, done: false },
    ];
    setMonth(updatedMonth);
    setNewMonth({ target: ""});

    try {
      const monthRef = doc(db, "month", currentUser.uid);
      await setDoc(monthRef, { month: updatedMonth });
    } catch (error) {
      console.error("Erreur lors de l'enregistrement :", error);
    }
  };

  const handleDeletemonth = async (monthId) => {
    const updatedMonth = month.filter((mo) => task.id !== monthId);
    setMonth(updatedMonth);

    try {
      const monthRef = doc(db, "month", currentUser.uid);
      await updateDoc(monthRef, { month: updatedMonth });
    } catch (error) {
      console.error("Erreur lors de la suppression :", error);
    }
  };

  return (
    <div className="quick-draft p-20 bg-white rad-10">
      <h2>Liste des objectifs mensuels</h2>
      <div className="input-section">
        <input
          type="text"
          placeholder="üìë Ajouter un nouvel objectif mensuel ici..."
          className="input w-full max-w-xs"
          value={newMonth.target}
          onChange={(e) => setNewMonth({ ...newMonth, target: e.target.value })}
        />
        <button onClick={handleAddTask} className="btn btn-add">
          AJOUTER
        </button>
      </div>

      <ul className="todos-list">
        {month.map((task) => (
          <li key={task.id} className={`todo-item ${task.done ? "done" : ""}`}>
            <span>{task.month}</span>
            <button onClick={() => handleDeleteTask(task.id)} className="btn btn-delete">
              Supprimer
            </button>
          </li>
        ))}
      </ul>

      <div className="summary">
        <p>
          Objectifs termin√©s :{" "}
          <span>
            {month.filter((task) => task.done).length} / {month.length}
          </span>
        </p>
      </div>
    </div>
  );
};

export default Calendrier;
