import React from "react";
import "./style.css";
import "./normalize.css";
import "./all.min.css";
import "./login&register/css/register-login.css";
import { Link } from "react-router-dom";
function Register() {
  return (
    <div className="mainContainer">
      <div className="mainBody">
        <img className="logo" src="./login&register/images/habit-tracker-gif-looponce.gif" alt="Habit tracker gif" />
        <h1>Register new account</h1>
        <form id="registerForm">
          <label htmlFor="username">Username:</label>
          <input type="text" id="username" name="username" required />

          <label htmlFor="password">Password:</label>
          <p>
            <input type="password" name="password" id="password" required />
            <i className="far fa-eye" id="togglePassword1" style={{ marginLeft: "-30px", cursor: "pointer" }}></i>
          </p>
          <span id="passwordMessage" style={{ color: "red" }}> </span>

          <label htmlFor="confirmPassword">Confirm Password:</label>
          <p>
            <input type="password" name="confirmPassword" id="confirmPassword" required />
            <i className="far fa-eye" id="togglePassword2" style={{ marginLeft: "-30px", cursor: "pointer" }}></i>
          </p>
          <span id="passwordConfirmMessage" style={{ color: "red" }}></span>

          <input type="submit" id="registerButton" name="registerButton" value="Register" />
        </form>
        <p className="link">Already have an account?<Link to="/login"></Link>login here</p>
      </div>
    </div>
  );
}

export default Register;
