import React, { useState, useEffect } from "react";
import "./framework.css";
import "./master.css";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import multipe from "./multiple-users-silhouette.png";
import friend from "./friends.png";
import team1 from "./team-01.png";
import team2 from "./team-02.png";
import { Link } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { doc, getDoc, setDoc, updateDoc } from "firebase/firestore";
import { db } from "../fireba/firebase";
export default function Challenge() {
  const { currentUser } = useAuth();
  const [openPopupId, setOpenPopupId] = useState(null); // State for open popups
  const [challenges, setChallenges] = useState([]); // State for challenges
  const [newChallenge, setNewChallenge] = useState({
    title: "",
    date: "",
    description: "",
    progress: 0,
    action: "",
  });
  const [searchQuery, setSearchQuery] = useState(""); // Search query
  const [filteredChallenges, setFilteredChallenges] = useState([]); // Filtered challenges

  // Load user data from Firebase
  const loadUserData = async () => {
    try {
      if (!currentUser?.uid) {
        throw new Error("User not logged in.");
      }

      const challengeRef = doc(db, "challenge", currentUser.uid);
      const challengeSnap = await getDoc(challengeRef);

      if (challengeSnap.exists()) {
        setChallenges(challengeSnap.data().challenges || []);
      } else {
        console.log("No challenges found.");
      }
    } catch (err) {
      console.error("Failed to load data:", err);
    }
  };

  useEffect(() => {
    loadUserData();
  }, [currentUser]);

  // Filter challenges based on search query
  useEffect(() => {
    if (!searchQuery.trim()) {
      setFilteredChallenges(challenges);
    } else {
      const query = searchQuery.toLowerCase();
      setFilteredChallenges(
        challenges.filter(
          (challenge) =>
            challenge.title.toLowerCase().includes(query) ||
            challenge.description.toLowerCase().includes(query) ||
            challenge.action.toLowerCase().includes(query)
        )
      );
    }
  }, [searchQuery, challenges]);

  // Add a new challenge
  const handleAddChallenge = async () => {
    if (
      !newChallenge.title ||
      !newChallenge.date ||
      !newChallenge.description ||
      !newChallenge.action
    ) {
      alert("Please fill all fields.");
      return;
    }

    const updatedChallenges = [
      ...challenges,
      {
        id: challenges.length + 1,
        ...newChallenge,
        team: ["team1.png", "team2.png"], // Default team members
      },
    ];

    try {
      const challengeRef = doc(db, "challenge", currentUser.uid);
      await setDoc(challengeRef, { challenges: updatedChallenges });
      setChallenges(updatedChallenges);
      setNewChallenge({ title: "", date: "", description: "", progress: 0, action: "" });
    } catch (error) {
      console.error("Error saving challenge:", error);
    }
  };

  // Toggle Popup
  const togglePopup = (challengeId) => {
    setOpenPopupId((prevId) => (prevId === challengeId ? null : challengeId));
  };

  return (
    <div className="page d-flex">
      {/* Sidebar */}
      <div className="sidebar bg-white p-20 p-relative">
        <h3 className="p-relative txt-c mt-0">habit-up</h3>
        <ul>
          <li>
            <Link to="/dashboard" className="d-flex align-center fs-14 c-black rad-6 p-10">
              <img src={photo} style={{ width: "24px" }} alt="Dashboard Icon" />
              <span>Dashboard</span>
            </Link>
          </li>
          <li>
            <Link to="/setting" className="d-flex align-center fs-14 c-black rad-6 p-10">
              <img src={setting} style={{ width: "24px" }} alt="Settings Icon" />
              <span>Setting</span>
            </Link>
          </li>
          <li>
            <Link to="/yourhabits" className="d-flex align-center fs-14 c-black rad-6 p-10">
              <img src={change} style={{ width: "24px" }} alt="Habit Icon" />
              <span>Habit</span>
            </Link>
          </li>
          <li>
            <Link to="/challenges" className="d-flex align-center fs-14 c-black rad-6 p-10">
              <img src={multipe} style={{ width: "24px" }} alt="Challenge Icon" />
              <span>Challenge</span>
            </Link>
          </li>
          <li>
            <Link to="/friends" className="d-flex align-center fs-14 c-black rad-6 p-10">
              <img src={friend} style={{ width: "24px" }} alt="Friends Icon" />
              <span>Friends</span>
            </Link>
          </li>
        </ul>
      </div>

      {/* Contenu principal */}
      <div className="content w-full">
        <h1 className="p-relative">Community Challenges</h1>

        {/* Recherche */}
        <input
          type="search"
          placeholder="Search challenges..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar d-block mb-20 p-10 rad-6"
        />

        {/* Formulaire d'ajout */}
        <div className="add-challenge-form bg-white p-20 rad-6 m-20">
          <h3>Add a New Challenge</h3>
          <input
            type="text"
            placeholder="Title"
            value={newChallenge.title}
            onChange={(e) => setNewChallenge({ ...newChallenge, title: e.target.value })}
            className="d-block mb-10 w-full"
          />
          <input
            type="text"
            placeholder="Date"
            value={newChallenge.date}
            onChange={(e) => setNewChallenge({ ...newChallenge, date: e.target.value })}
            className="d-block mb-10 w-full"
          />
          <input
            type="text"
            placeholder="Description"
            value={newChallenge.description}
            onChange={(e) =>
              setNewChallenge({ ...newChallenge, description: e.target.value })
            }
            className="d-block mb-10 w-full"
          />
          <input
            type="text"
            placeholder="Action"
            value={newChallenge.action}
            onChange={(e) => setNewChallenge({ ...newChallenge, action: e.target.value })}
            className="d-block mb-10 w-full"
          />
          <button onClick={handleAddChallenge} className="btn bg-blue c-white">
            Add Challenge
          </button>
        </div>
        <div className="projects-page d-grid m-20 gap-20">
          {filteredChallenges.map((challenge) => (
            <div key={challenge.id} className="project bg-white p-20 rad-6 p-relative">
              <span className="date fs-13 c-grey">{challenge.date}</span>
              <h4 className="m-0">{challenge.title}</h4>
              <p className="c-grey mt-10 mb-10 fs-14">{challenge.description}</p>
              <div className="team">
                {challenge.team.map((member, index) => (
                  <a key={index} href="#!">
                    <img src={team2 } 
                    />
                  </a>
                ))}
              </div>
              <div className="do d-flex">
                <span className="fs-13 rad-6 bg-eee">{challenge.action}</span>
              </div>
            </div>
          ))}
       <div class="do d-flex">
              <span class="fs-13 rad-6 bg-eee">learn new Language</span>
            </div>
            <div class="info between-flex">
              <div class="prog bg-eee">
                <span class="bg-red" style="width: 50%"></span>
              </div>
              <div class="fs-14 c-grey">
                
                <input type="submit" value="See More.." class="see-more
                 d-block fs-14 bg-blue c-white w-fit btn-shape" onclick="OpenPopup(1)"/>
              </div>
            </div>
            <div class="popup d-flex p-20 bg-eee " id="popup-1">
              <h2 class="mt-0 mb-10 p-10">challenge Details </h2>
              <p class="c-black mt-10 mb-10 fs-14">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Officiis rem ab 
                corrupti quos repellat vero nam reiciendis impedit eaquer doloremque alias,
                 quo adipisci in, dignissimos esse odio, dicta sequi quod!
              </p>
    
              <input class="enrol d-block fs-14 bg-blue c-white w-fit btn-shape " type="submit" value="enrol"/>
              <input class="close d-block fs-14 bg-blue c-white w-fit btn-shape " type="submit" value="close" 
              onclick="ClosePopup(1)"/>
          </div>
          </div>
           <div class="project bg-white p-20 rad-6 p-relative">
            <span class="date fs-13 c-grey">15/6/2022</span>
            <h4 class="m-0">Academy Portal</h4>
            <p class="c-grey mt-10 mb-10 fs-14">Academy Portal Project Design And Programming</p>
            <div class="team">
              <a href="#"><img src="imgs/team-01.png" alt="" /></a>
              <a href="#"><img src="imgs/team-02.png" alt="" /></a>
              <a href="#"><img src="imgs/team-03.png" alt="" /></a>
            </div>
            <div class="do d-flex">
              <span class="fs-13 rad-6 bg-eee">Go to Gym</span>
            </div>
            <div class="info between-flex">
              <div class="prog bg-eee">
                <span class="bg-green" style={{width: "80%"}}></span>
              </div>
              <div class="fs-14 c-grey">
                <input type="submit" value="See More.." class="see-more
                 d-block fs-14 bg-blue c-white w-fit btn-shape" onclick="OpenPopup(2)"/>
              </div>
            </div>
            <div class="popup d-flex p-20 bg-eee " id="popup-2">
              <h2 class="mt-0 mb-10 p-10">challenge Details </h2>
              <p class="c-black mt-10 mb-10 fs-14">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Officiis rem ab 
                corrupti quos repellat vero nam reiciendis impedit eaquer doloremque alias,
                 quo adipisci in, dignissimos esse odio, dicta sequi quod!
              </p>
    
              <input class="enrol d-block fs-14 bg-blue c-white w-fit btn-shape " type="submit" value="enrol"/>
              <input class="close d-block fs-14 bg-blue c-white w-fit btn-shape " type="submit" value="close" 
              onclick="ClosePopup(2)"/>
          </div>
          </div>
          <div class="project bg-white p-20 rad-6 p-relative">
            <span class="date fs-13 c-grey">15/6/2022</span>
            <h4 class="m-0">Chatting Application</h4>
            <p class="c-grey mt-10 mb-10 fs-14">Chatting Application Project Design</p>
            <div class="team">
              <a href="#"><img src="imgs/team-01.png" alt="" /></a>
              <a href="#"><img src="imgs/team-02.png" alt="" /></a>
              <a href="#"><img src="imgs/team-03.png" alt="" /></a>
            </div>
            <div class="do d-flex">
              <span class="fs-13 rad-6 bg-eee">Learn Design</span>
            </div>
            <div class="info between-flex">
              <div class="prog bg-eee">
                <span class="bg-blue" style={{width: "100%"}}></span>
              </div>
              <div class="fs-14 c-grey">
                <input type="submit" value="See More.." class="see-more
                 d-block fs-14 bg-blue c-white w-fit btn-shape" onclick="OpenPopup(3)"/>
              </div>
            </div>
            <div class="popup d-flex p-20 bg-eee " id="popup-3">
              <h2 class="mt-0 mb-10 p-10">challenge Details </h2>
              <p class="c-black mt-10 mb-10 fs-14">
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Officiis rem ab 
                corrupti quos repellat vero nam reiciendis impedit eaquer doloremque alias,
                 quo adipisci in, dignissimos esse odio, dicta sequi quod!
              </p>
    
              <input class="enrol d-block fs-14 bg-blue c-white w-fit btn-shape " type="submit" value="enrol"/>
              <input class="close d-block fs-14 bg-blue c-white w-fit btn-shape " type="submit" value="close" 
              onclick="ClosePopup(3)"/>
          </div>
          </div>
          <div class="project bg-white p-20 rad-6 p-relative">
            <span class="date fs-13 c-grey">15/6/2022</span>
            <h4 class="m-0">Ahmed Dashboard</h4>
            <p class="c-grey mt-10 mb-10 fs-14">Ahmed Dashboard Project Design And Programming And Hosting</p>
            <div class="team">
              <a href="#"><img src="imgs/team-01.png" alt="" /></a>
              <a href="#"><img src="imgs/team-02.png" alt="" /></a>
              <a href="#"><img src="imgs/team-03.png" alt="" /></a>
              <a href="#"><img src="imgs/team-04.png" alt="" /></a>
            </div>
            <div class="do d-flex">
              <span class="fs-13 rad-6 bg-eee">Stop smmoking</span>
       
            </div>
            </div>
      </div>
    </div>
  );
}
