import React, { useState, useMemo, useRef,Link } from "react";
import { ref, push, set } from "firebase/database";
import Select from 'react-select';
import logo1 from "./habit-tracker-gif-looponce.gif";
import countryList from 'react-select-country-list';
import { Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom"; // Importation de useNavigate
import { db } from "../fireba/firebase"; // Assurez-vous que `db` est correctement exporté depuis firebase.js
import "./register-login.css";

export default function Registre() {
  const [value, setValue] = useState('');
  const options = useMemo(() => countryList().getData(), []);

  const changeHandler = value => {
    setValue(value);
  }

  const nameRef = useRef();
  const countryRef = useRef();
  const ageref = useRef();
  const passwordRef = useRef(); // Déclaration de passwordRef
  const passwordConfirmRef = useRef(); // Déclaration de passwordConfirmRef
  const emailRef = useRef(); // Déclaration de emailRef (si nécessaire)

  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [name, setName] = useState("");
  const [country, setcountry] = useState("");
  const [age, setage] = useState("");
  const [loader, setLoader] = useState(false);
  const [alert, setAlert] = useState(false);
  const [loading, setLoading] = useState(false); // Déclaration de setLoading
  const [showPassword, setShowPassword] = useState(false); // Déclaration de showPassword
  const [showPasswordConfirm, setShowPasswordConfirm] = useState(false); // Déclaration de showPasswordConfirm

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);

    // Sauvegarder le message dans Firebase
    const messagesRef = ref(db, 'messages');
    const newMessageRef = push(messagesRef);
    set(newMessageRef, {
      name: name,
      country: country,
      age: age,
    })
      .then(() => {
        setLoader(false);
        setAlert(true);

        // Masquer l'alerte après 3 secondes
        setTimeout(() => {
          setAlert(false);
        }, 3000);

        // Réinitialiser le formulaire
        setName("");
        setcountry("");
        setage("");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });

    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError("Passwords do not match");
    }
    if (nameRef.current.value === "") {
      return setError("Please enter a name");
    }
    if (!value) {
      return setError("Please select a country");
    }
    if (ageRef.current.value === "") {
      return setError("Please enter your age");
    }

    try {
      setError("");
      setLoading(true);
      navigate("/Dashboard", { state: { name, country, age } });
    } catch (error) {
      setError("Failed to create an account");
      console.error(error);
      setError(error.message || "Failed to create an account");
    } finally {
      setLoading(false);
    }
  }
  return (
    <div className="loginmainContainer">
      <div className="loginmainBody">
        <img className="logo" src={logo1} alt="Habit tracker gif" />
        <h1>Register new account</h1>
        {error && <Alert variant="danger">{error}</Alert>}
        <form id="registerForm" onSubmit={handleSubmit}>
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" required ref={emailRef} />
          <label htmlFor="password">Password:</label>
          <p>
            <input
              type={showPassword ? "text" : "password"}
              name="password"
              id="password"
              required
              ref={passwordRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword1"
              style={{ marginLeft: "-30px", cursor: "pointer" }}
              onClick={() => setShowPassword((prev) => !prev)}
            ></i>
          </p>

          <label htmlFor="confirmPassword">Confirm Password:</label>
          <p>
            <input
              type={showPasswordConfirm ? "text" : "password"}
              name="confirmPassword"
              id="confirmPassword"
              required
              ref={passwordConfirmRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword2"
              style={{ marginLeft: "-30px", cursor: "pointer" }}
              onClick={() => setShowPasswordConfirm((prev) => !prev)}
            ></i>
          </p>

          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" required ref={nameRef} />
          <label htmlFor="country">Country</label>
          <Select options={options} value={value} onChange={changeHandler}ref={nameRef} />
          <label htmlFor="age">Age:</label>
          <input type="number" id="age" name="age" required ref={ageref} />
          <input disabled={loader} type="submit" id="registerButton" name="registerButton" value="Complete" />
        </form>
        <p className="loginlink">
          Already have an account?<Link to="/login"> login here</Link>
        </p>
      </div>
    </div>
  );
}
