import React from "react";
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from "react-router-dom";
import "./style.css";
import "./normalize.css";
import "./all.min.css";
import Login from "./login";

const Header = () => (
  <div className="header">
    <div className="container">
      <img className="logo" src="/images/logo.png" alt="Logo" />
      <div className="links">
        <ul>
          <li><Link to="/login">Login</Link></li>
          <li><Link to="/register">Register</Link></li>
          <li><Link to="/#about-us">About</Link></li>
          <li><Link to="/#contact">Contact</Link></li>
        </ul>
      </div>
    </div>
  </div>
);

const Footer = () => (
  <footer className="footer">
    <div className="container">
      <p>© 2024 Habit-Up. All Rights Reserved.</p>
    </div>
  </footer>
);

const Landing = () => (
  <div className="landing">
    <h1>Welcome to Habit-Up</h1>
    <p>Your guide to track your habit, challenge yourself...</p>
    <Link to="/login" className="btn-primary">Get Started</Link>
  </div>
);

const Home = () => (
  <>
    <Landing />
    <div className="features">
      <h2>Features</h2>
      {/* Ajoutez vos autres sections ici */}
    </div>
  </>
);

const App = () => {
  const location = useLocation(); // Récupère la route actuelle

  const isLoginPage = location.pathname === "/login"; // Vérifie si on est sur la page de login

  return (
    <div className="app">
      {!isLoginPage && <Header />} {/* Affiche le header uniquement si on n'est pas sur /login */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
      </Routes>
      {!isLoginPage && <Footer />} {/* Affiche le footer uniquement si on n'est pas sur /login */}
    </div>
  );
};

export default function MainApp() {
  return (
    <Router>
      <App />
    </Router>
  );
}
