import React, { useRef, useState } from "react";
import "./style.css";
import "./normalize.css";
import "./all.min.css";
import "./login&register/css/register-login.css";
import { Alert } from "react-bootstrap";
import { useAuth } from "./context/AuthContext";
import { Link, useNavigate,useLocation } from "react-router-dom";
import logo1 from "./images/habit-tracker-gif-looponce.gif";
function Login() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const { login } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const redirectPath = location.state?.path || "/";
  async function handleSubmit(e) {
    e.preventDefault();

    try {
      setError("");
      setLoading(true);
      await login(emailRef.current.value, passwordRef.current.value);
      navigate(redirectPath);
    } catch {
      setError("Failed to log in");
    }

    setLoading(false);
  }
  return (
    <div id="mainContainer">
      <div className="mainBody">  
        <img id="logo" src={logo1} alt="Description"/>
        <h1>Welcome Back.</h1>
        {error && <Alert variant="danger">{error}</Alert>}
        <form  onSubmit={handleSubmit}>
          <label htmlFor="username">Username:</label>
          <input type="text" id="username" name="username" required  ref={emailRef}/>
          <label htmlFor="password">Password:</label>
          <p>
            <input
              type="password"
              name="password"
              id="password"
              required
              ref={passwordRef}
            />
            <i
              className="far fa-eye"
              id="togglePassword"
              style={{ marginLeft: '-30px', cursor: 'pointer' }}
            ></i>
          </p>
          <input disabled={loading}
            type="submit"
            id="loginButton"
            name="loginButton"
            value="Login"
            required
          />
        </form>
        <p className="link">
          Don't have an account?{' '}
          <Link to="/Registre">Register here</Link>       
        </p>
      </div>
    </div>
  );
}
export default Login;
