import { doc, getDoc,setDoc} from "firebase/firestore";
import React, { useState, useEffect,useRef} from "react";
import { Link, useNavigate} from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import { db } from "../fireba/firebase";
import "./framework.css";
import "./master.css";
import "./style1.css";
import "./dashbord.css";
import profile from "./avatar.png";
import welcome from "./welcome.png";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import friend from "./friends.png";
import multipe from "./multiple-users-silhouette.png";
import check from"./check.png";
import trash from "./trash-can.png"

export default function Dashboard() {
  const [loader, setLoader] = useState(false);
  const [tasks, settasks] = useState([]);
  const { currentUser } = useAuth(); 
  const [userData, setUserData] = useState({ name: "", age: "", country: "" });
  const [error, setError] = useState("");
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [newtask,setnewtask] = useState({
    title: "",
    remarque:""
  });
  const titleRef = useRef();
  const remarqueref = useRef();
  useEffect(() => {
    async function loadUserData() {
      const data = await fetchUserData(currentUser.uid); // Replace with dynamic ID
      if (data) {
        setUserData(data);
      }
    }
    loadUserData();
  }, []);

  async function fetchUserData(userId) {
    const docRef1= doc(db, "users", userId);
    const docRef2 = doc(db, "tasks", userId);
    const docSnap1 = await getDoc(docRef1);
    const docSnap2 = await getDoc(docRef2);
    if (docSnap1.exists()||docSnap2.exists()) {
      return docSnap1.data()||docSnap2;
    } else {
      console.error("No such document!");
      return null;
    }
  }

  async function handleLogout() {
    setError("");
    try {
      await logout();
      navigate("/login");
    } catch {
      setError("Failed to log out");
    }
  }
  const handleAddChallenge = () => {
    if (!newtask.title||
      !newtask.remarque
    ) {
      alert("Please fill the task");
      return;
    }
  
    const newId = tasks.length + 1;
    settasks([
      ...tasks,
      {
        id: newId,
        ...newtask,
      },
    ]);
    setnewtask({ title: "" ,remarque:""});
  };
  const handleMarkAsDone = (taskId) => {
    settasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, done: !task.done } : task
      )
    );
  };
  const handleDeleteTask = (taskId) => {
    settasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
  };
  async function handleSubmit(e) {
    e.preventDefault();
    try {
      setError(""); 
      setLoader(true); 
  
      const id = currentUser.uid; 
      const userRef = doc(db, "tasks", id);
      await setDoc(userRef, {
        title: titleRef.current.value,
       remarque: remarqueref.current.value,
       
      });
    } catch (error) {
      setError("Failed to save user data");
    } finally {
      setLoader(false);
    }
  }
  return (
    <div class="page d-flex">
        <div class="sidebar bg-white p-20 p-relative">
          <h3 class="p-relative txt-c mt-0">habit-up</h3>
          <ul>
      <li>
      <Link to="/dashboard" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={photo} style={{ width: '24px' }}/><span>Dashboard</span></Link>
          </li>
          <li>
          <Link to="/setting" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={setting} style={{ width: '24px' }}/><span>Setting</span></Link>
          </li>
          <li>
          <Link to="/yourhabits" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={change} style={{ width: '24px' }}/><span>habit</span></Link>
          </li>
          <li>
          <Link to="/challenges" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={multipe} style={{ width: '24px' }}/><span>challenge</span></Link>
          </li>
          <li>
          <Link to="/friends" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={friend} style={{ width: '24px' }}/><span>friends</span></Link>
          </li>
      </ul>
        </div>
      <div className="content w-full">
        <div className="head bg-white p-15 between-flex">
          <div className="icons d-flex align-center">
            <img
              src={profile}
              onClick={handleLogout}
              style={{ cursor: "pointer" }}
              alt="Profile Icon"
            />
            <h6 style={{ marginLeft: "10px" }}>Logout</h6>
          </div>
        </div>
        <h1 className="p-relative">Profile</h1>
        <div className="wrapper d-grid gap-20">
          <div className="welcome bg-white rad-10 txt-c-mobile block-mobile">
            <div className="intro p-20 d-flex space-between bg-eee">
              <h2 className="m-0">Welcome</h2>
              <img className="hide-mobile" src={welcome} alt="Welcome" />
            </div>
            <img src={profile} alt="Profile Avatar" className="avatar" />
            <div className="body txt-c d-flex p-20 mt-20 mb-20 block-mobile">
              <div>
                {userData.name} <span className="d-block c-grey fs-14 mt-10">name</span>
              </div>
              <div>
                {userData.age} <span className="d-block c-grey fs-14 mt-10">age</span>
              </div>
              <div>
                {userData.country}
                <span className="d-block c-grey fs-14 mt-10">country</span>
              </div>
            </div>
            </div>
            <div class="tasks p-20 bg-white rad-10">
                <h2 class="mt-0 mb-20">Today Tasks</h2>
                <input id="todo-input" type="text" placeholder="📑 Add Today Tasks here .."
              class="input w-full max-w-xs"onChange={(e) => setnewtask({ ...newtask, title: e.target.value })} style={{ margin: "5px" }} ref={titleRef}/>
              <input id="todo-input" type="text" placeholder="📑 Add Today Remarque here .."
              class="input w-full max-w-xs"onChange={(e) => setnewtask({ ...newtask, remarque: e.target.value })} style={{ margin: "5px" }} ref={remarqueref}/>
                <input class="save d-block fs-14 bg-blue c-white b-none w-fit btn-shape" type="submit" value="Add"  onClick={handleSubmit}/>
          {tasks.map((task) => (
  <div
    key={task.id}
    className={`task-row between-flex ${task.done ? "done" : ""}`}
  >
    <div className="info">
      <h3 className="mt-0 mb-5 fs-15">{task.title}</h3>
      <p className="m-0 c-grey">{task.remarque}</p>
    </div>
    <img
      src={check}
      style={{ width: "24px", cursor: "pointer" }}
      alt="Mark as done"
      onClick={() => handleMarkAsDone(task.id)}
    />
    <img
      src={trash}
      style={{ width: "24px", cursor: "pointer" }}
      alt="Delete task"
      onClick={() => handleDeleteTask(task.id)}
    />
  </div>
))}
            </div>
            <div class="quick-draft p-20 bg-white rad-10">
                <h2 class="mt-0 mb-10">Quick Note</h2>
                <p class="mt-0 mb-20 c-grey fs-15">Write A Quick Note,Ideas,Events...</p>
                <form>
                  <input class="d-block mb-20 w-full p-10 b-none bg-eee rad-6" type="text" placeholder="Title" />
                  <textarea class="d-block mb-20 w-full p-10 b-none bg-eee rad-6" placeholder="Your Thought"></textarea>
                  <input class="save d-block fs-14 bg-blue c-white b-none w-fit btn-shape" type="submit" value="Save" />
                </form>
            </div>
            <div class="quick-draft p-20 bg-white rad-10">
            <h2 className="a">Monthly Targets List</h2>
      <div class="input-section">
          <input id="todo-input" type="text" placeholder="📑 Add your new monthly target here.."
              class="input w-full max-w-xs" />
          <button id="add-button" class="btn btn-add " >
              ADD
          </button>
      </div>
      <div class="todos-filter">
                  <button id="delete-selected" class="btn btn-ghost delete-selected-btn">
                      Delete Selected
                  </button>
                  <button id="delete-all" class="btn btn-ghost delete-all-btn">
                      Delete All
                  </button>
                  </div>
                  <ul id="all-todos" class="todos-list">
      </ul>
      <div class="filters">
      <button class="dropbtn">Filter</button>
                      <div class="dropdown-content">
                          <a href="#" id="all">All</a>
                          <a href="#" id="rem">Pending</a>
                          <a href="#" id="com">Completed</a>
                      </div>
                      <div class="completed-task">
                      <p>Completed: <span id="c-count">0</span></p>
                  </div>
                  <div class="remaining-task">
                      <p>Total Targets: <span id="r-count">0</span></p>
                  </div>
            </div>
            </div>
              </div>
              <div class="modal  " id="modal" style={{position: "absolute",height:"100%", width: "100%"}}>
                <div class="modal-header">
                  <div class="title">Calendar</div>
                  <button data-close-button class="close-button">&times;</button>
                </div>
                <div class="modal-body d-flex " >
                 
                        <div class="container ">
                          <div class="left">
                            <div class="calendar">
                              <div class="month">
                                <i class="fas fa-angle-left prev"></i>
                                <div class="date">december 2015</div>
                                <i class="fas fa-angle-right next"></i>
                              </div>
                              <div class="weekdays">
                                <div>Sun</div>
                                <div>Mon</div>
                                <div>Tue</div>
                                <div>Wed</div>
                                <div>Thu</div>
                                <div>Fri</div>
                                <div>Sat</div>
                              </div>
                              <div class="days"></div>
                              <div class="goto-today">
                                <div class="goto">
                                  <input type="text" placeholder="mm/yyyy" class="date-input" />
                                  <button class="goto-btn">Go</button>
                                </div>
                                <button class="today-btn">Today</button>
                              </div>
                            </div>
                          </div>
                          <div class="right">
                            <div class="today-date">
                              <div class="event-day">wed</div>
                              <div class="event-date">12th december 2022</div>
                            </div>
                            <div class="events"></div>
                            <div class="add-event-wrapper">
                              <div class="add-event-header">
                                <div class="title">Add Event</div>
                                <i class="fas fa-times close"></i>
                              </div>
                              <div class="add-event-body">
                                <div class="add-event-input">
                                  <input type="text" placeholder="Event Name" class="event-name" />
                                </div>
                                <div class="add-event-input">
                                  <input
                                    type="text"
                                    placeholder="Event Time From"
                                    class="event-time-from"
                                  />
                                </div>
                                <div class="add-event-input">
                                  <input
                                    type="text"
                                    placeholder="Event Time To"
                                    class="event-time-to"
                                  />
                                </div>
                              </div>
                              <div class="add-event-footer">
                                <button class="add-event-btn">Add Event</button>
                              </div>
                            </div>
                          </div>
                          <button class="add-event">
                            <i class="fas fa-plus"></i>
                          </button>
                        </div>
                    
                        <div class="credits">
                        </div>
                
                      </div>
              </div>
                <div id="overlay"></div>
            
            </div>
        
          </div>
  );
};
