import React from "react";
import "./style.css";
import "./normalize.css";
import "./all.min.css";
import { Alert } from "react-bootstrap";
import "./login&register/css/register-login.css";
import { useRef, useState } from "react";
import { useAuth } from "./context/AuthContext";
import { Link ,useNavigate} from "react-router-dom";
import logo1 from "./images/habit-tracker-gif-looponce.gif";
export default function Registre() {
  const emailRef = useRef();
  const passwordRef = useRef();
  const passwordConfirmRef = useRef();
  const { signup } = useAuth();
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  async function handleSubmit (e) {
    e.preventDefault();
    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError("Passwords do not match");
    }
    try {
      setError("");
      await signup(emailRef.current.value, passwordRef.current.value);
      navigate("/dashbord");
    } catch {
      setError("Failed to create an account");
    }
    setLoading(false);
  }

  return (
    <div className="mainContainer">
      <div className="mainBody">
        <img className="logo" src={logo1} alt="Habit tracker gif" />
        <h1>Register new account</h1>
        {error && <Alert variant="danger">{error}</Alert>}
        <form id="registerForm"onSubmit={handleSubmit}>
          <label htmlFor="username">Username:</label>
          <input type="text" id="username" name="username" required ref={emailRef}/>

          <label htmlFor="password">Password:</label>
          <p>
            <input type="password" name="password" id="password" required  ref={passwordRef} />
            <i className="far fa-eye" id="togglePassword1" style={{ marginLeft: "-30px", cursor: "pointer" }}></i>
          </p>
          <span id="passwordMessage" style={{ color: "red" }}> </span>

          <label htmlFor="confirmPassword">Confirm Password:</label>
          <p>
            <input type="password" name="confirmPassword" id="confirmPassword" required />
            <i className="far fa-eye" id="togglePassword2" style={{ marginLeft: "-30px", cursor: "pointer" }}> 
            </i>
          </p>
          <span id="passwordConfirmMessage" style={{ color: "red" }}></span>

          <input disabled={loading} type="submit" id="registerButton" name="registerButton" value="Register" />
        </form>
        <p className="link">Already have an account?<Link to="/login">login here</Link></p>
      </div>
    </div>
  );
}
