import React, { useState } from 'react';
import './index.scss'; // Assurez-vous d'importer le fichier CSS ici

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [habits, setHabits] = useState([]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleAddHabit = () => {
    if (inputValue.trim() === '') {
      alert('Please enter a habit before submitting!');
    } else {
      setHabits([...habits, inputValue]);
      setInputValue('');
    }
  };

  return (
    <div className='categories-container'>
    <nav className="navigation-bar">
      <a href="#" className="nav-link">Home</a>
      <a href="#" className="nav-link">Link 1</a>
      <a href="#" className="nav-link">Link 2</a>
      <a href="#" className="nav-link">Link 3</a>
    </nav>
    <div className='categories-container'>
      <header className="w3-container w3- w3-center" style={{ padding: '128px 16px' }}>
        <h1 className="w3-margin w3-jumbo">START PAGE</h1>
        <p className="w3-xlarge">Template by w3.css</p>
        <button className="w3-button w3-black w3-padding-large w3-large w3-margin-top">Get Started</button>
      </header>
      
      <div className='category-container'>
        <div className='category-body-container'>
          <h2>Add a New Habit</h2>
          <input 
            className='i'
            type="text" 
            placeholder="Type a new habit..." 
            value={inputValue}
            onChange={handleInputChange} 
          />
          <button className='g' onClick={handleAddHabit}>Add Habit</button>
          <div className="habits-grid">
            {habits.map((habit, index) => (
              <div key={index} className="habit-card">
                <p>{habit}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
    </div>
  );
};

export default App;
