import { useNavigate } from "react-router-dom";
import React, { useState, useMemo, useRef } from "react";
import { Alert } from "react-bootstrap";
import logo1 from "./habit-tracker-gif-looponce.gif";
import "./register-login.css";
import Select from "react-select";
import countryList from "react-select-country-list";
import { collection, addDoc } from "firebase/firestore";
import { db } from "../fireba/firebase"; // Assurez-vous que ce chemin est correct

export default function Complete() {
  const [value, setValue] = useState("");
  const options = useMemo(() => countryList().getData(), []);
  const changeHandler = (value) => setValue(value);

  const nameRef = useRef();
  const ageRef = useRef();
  const emailRef = useRef();

  const [error, setError] = useState("");
  const navigate = useNavigate();
  const [loader, setLoader] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    if (!value) {
      setError("Please select a country");
      return;
    }

    try {
      setError(""); // Réinitialisation des erreurs
      setLoader(true);

      // Ajouter les données utilisateur dans Firestore
      await addDoc(collection(db, "users"), {
        name: nameRef.current.value,
        country: value.label,
        age: ageRef.current.value,
        email: emailRef.current.value,
      });

      // Rediriger vers le tableau de bord
      navigate("/Dashboard", {
        state: {
          name: nameRef.current.value,
          country: value.label,
          age: ageRef.current.value,
        },
      });
    } catch (error) {
      console.error("Signup error:", error);
      setError("Failed to create an account");
    } finally {
      setLoader(false);
    }
  }

  return (
    <div className="loginmainContainer">
      <div className="loginmainBody">
        <img className="logo" src={logo1} alt="Habit tracker gif" />
        <h1>Please complete registration</h1>
        {error && <Alert variant="danger">{error}</Alert>}
        <form id="registerForm" onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input type="text" id="name" name="name" required ref={nameRef} />
          <label htmlFor="country">Country:</label>
          <Select options={options} value={value} onChange={changeHandler} />
          <label htmlFor="age">Age:</label>
          <input type="number" id="age" name="age" required ref={ageRef} />
          <input
            disabled={loader}
            type="submit"
            id="registerButton"
            name="registerButton"
            value="Complete"
          />
        </form>
      </div>
    </div>
  );
}
