import React, { useState } from 'react';
import './index.scss';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [habits, setHabits] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = () => {
    if (inputValue.trim()) {
      // Ajouter l'habitude à la liste
      setHabits([...habits, inputValue]);
      setInputValue('');
      setSubmitted(false);  // Réinitialiser la soumission pour cacher le bloc d'ajout
    } else {
      alert("Please enter a habit before submitting!");
    }
  };

  return (
    <div className="categories-container">
      {/* Bloc d'ajout dynamique */}
      <div className="new-habit-container">
        <h2>Add new bad habit</h2>
        <input 
          type="text" 
          placeholder="Type something..." 
          value={inputValue}
          onChange={handleInputChange} 
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>

      {/* Affichage des habitudes ajoutées */}
      {habits.map((habit, index) => (
        <div key={index} className={`category-container ${submitted ? 'translated' : ''}`}>
          <div className="category-body-container">
            <h2>{habit}</h2>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
