import "./framework.css";
import "./master.css";
import "./style1.css";
import profile from "./avatar.png"
import welcome from "./welcome.png"
import photo from "./profile-user.png"
import change from "./cigarette.png"
import setting from "./settings.png"
import friend from "./friends.png"
import multipe from "./multiple-users-silhouette.png"
import React from "react";
import { Link } from 'react-router-dom';
export default function Dashboard() {
  const openModalButtons = document.querySelectorAll('[data-modal-target]');
  const closeModalButtons = document.querySelectorAll('[data-close-button]');
  const overlay = document.getElementById('overlay')
  const modals = document.querySelectorAll('.modal.active')  
  const modal = button.closest('.modal')
  const calendar = document.querySelector(".calendar"),
    date = document.querySelector(".date"),
    daysContainer = document.querySelector(".days"),
    prev = document.querySelector(".prev"),
    next = document.querySelector(".next"),
    todayBtn = document.querySelector(".today-btn"),
    gotoBtn = document.querySelector(".goto-btn"),
    dateInput = document.querySelector(".date-input"),
    eventDay = document.querySelector(".event-day"),
    eventDate = document.querySelector(".event-date"),
    eventsContainer = document.querySelector(".events"),
    addEventBtn = document.querySelector(".add-event"),
    addEventWrapper = document.querySelector(".add-event-wrapper "),
    addEventCloseBtn = document.querySelector(".close "),
    addEventTitle = document.querySelector(".event-name "),
    addEventFrom = document.querySelector(".event-time-from "),
    addEventTo = document.querySelector(".event-time-to "),
    addEventSubmit = document.querySelector(".add-event-btn ");
      
  var todoList = []
  var comdoList = [];
  var remList = [];
  var addButton = document.getElementById("add-button")
  var todoInput = document.getElementById("todo-input")
  var deleteAllButton = document.getElementById("delete-all")
  var allTodos = document.getElementById("all-todos");
  var deleteSButton = document.getElementById("delete-selected")
  
  
  const eventsArr = [];
  let today = new Date();
  let activeDay;
  let month = today.getMonth();
  let year = today.getFullYear();
  
  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];
  return (
    <div class="page d-flex">
    <div class="sidebar bg-white p-20 p-relative">
      <h3 class="p-relative txt-c mt-0">habit-up</h3>
      <ul>
      <li>
      <Link to="/dashboard" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={photo} style={{ width: '24px' }}/><span>Dashboard</span></Link>
          </li>
          <li>
          <Link to="/setting" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={setting} style={{ width: '24px' }}/><span>Setting</span></Link>
          </li>
          <li>
          <Link to="/yourhabits" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={change} style={{ width: '24px' }}/><span>habit</span></Link>
          </li>
          <li>
          <Link to="/challenges" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={multipe} style={{ width: '24px' }}/><span>challenge</span></Link>
          </li>
          <li>
          <Link to="/friends" className=" d-flex align-center fs-14 c-black rad-6 p-10"><img src={friend} style={{ width: '24px' }}/><span>friends</span></Link>
          </li>
      </ul>
    </div>
        <div class="content w-full">
        <div class="head bg-white p-15 between-flex">
          <div class="icons d-flex align-center">
          <img src={profile}/><Link to="/login"><h6 style={{ marginLeft: '10px', cursor: 'pointer' }} >logout</h6></Link>
           
          </div>
        </div>
          
          <h1 class="p-relative">Profile</h1>
          <div class="wrapper d-grid gap-20">
            
            <div class="welcome bg-white rad-10 txt-c-mobile block-mobile">
              <div class="intro p-20 d-flex space-between bg-eee">
                <div>
                  <h2 class="m-0">Welcome</h2>
                            </div>
                <img class="hide-mobile" src={welcome } alt="" />
              </div>
              <img src={profile} alt="" class="avatar" />
              <div class="body txt-c d-flex p-20 mt-20 mb-20 block-mobile">
                <div>ghayeth-bhr <span class="d-block c-grey fs-14 mt-10">student</span></div>
                <div>22 <span class="d-block c-grey fs-14 mt-10">age</span></div>
                <div>Tunisia <span class="d-block c-grey fs-14 mt-10">country</span></div>
              </div>
            </div>  
              <div class="tasks p-20 bg-white rad-10">
                <h2 class="mt-0 mb-20">Today Tasks</h2>
                <div class="task-row between-flex">
                  <div class="info">
                    <h3 class="mt-0 mb-5 fs-15">Record One New Video</h3>
                    <p class="m-0 c-grey">Record Python Create Exe Project</p>
                  </div>
                  <i class="fa-regular fa-trash-can delete"></i>
                </div>
                <div class="task-row between-flex">
                  <div class="info">
                    <h3 class="mt-0 mb-5 fs-15">Write Article</h3>
                    <p class="m-0 c-grey">Write Low Level vs High Level Languages</p>
                  </div>
                  <i class="fa-regular fa-trash-can delete"></i>
                </div>
                <div class="task-row between-flex">
                  <div class="info">
                    <h3 class="mt-0 mb-5 fs-15">Finish Project</h3>
                    <p class="m-0 c-grey">Publish Academy Programming Project</p>
                  </div>
                  <i class="fa-regular fa-trash-can delete"></i>
                </div>
                <div class="task-row between-flex done">
                  <div class="info">
                    <h3 class="mt-0 mb-5 fs-15">Attend The Meeting</h3>
                    <p class="m-0 c-grey">Attend The Project Business Analysis Meeting</p>
                  </div>
                  <i class="fa-regular fa-trash-can delete"></i>
                </div>
                <div class="task-row between-flex">
                  <div class="info">
                    <h3 class="mt-0 mb-5 fs-15">Finish Lesson</h3>
                    <p class="m-0 c-grey">Finish Teaching Flex Box</p>
                  </div>
                  <i class="fa-regular fa-trash-can delete"></i>
                </div>
              </div>
              <div class="quick-draft p-20 bg-white rad-10">
                <h2 class="mt-0 mb-10">Quick Note</h2>
                <p class="mt-0 mb-20 c-grey fs-15">Write A Quick Note,Ideas,Events...</p>
                <form>
                  <input class="d-block mb-20 w-full p-10 b-none bg-eee rad-6" type="text" placeholder="Title" />
                  <textarea class="d-block mb-20 w-full p-10 b-none bg-eee rad-6" placeholder="Your Thought"></textarea>
                  <input class="save d-block fs-14 bg-blue c-white b-none w-fit btn-shape" type="submit" value="Save" />
                </form>
              </div>
              <div class="targets p-20 bg-white rad-10">
                  <header>
                      <h1>Monthly Targets List</h1>
          
                      <div class="input-section">
                          <input id="todo-input" type="text" placeholder="📑 Add your new monthly target here.."
                              class="input w-full max-w-xs" />
                          <button id="add-button" class="btn btn-add ">
                              ADD
                          </button>
                      </div>
                  </header>
                  <div class="todos-filter">
          
                      <button id="delete-selected" class="btn btn-ghost delete-selected-btn">
                          Delete Selected
                      </button>
                      <button id="delete-all" class="btn btn-ghost delete-all-btn">
                          Delete All
                      </button>
                  </div>
                  <ul id="all-todos" class="todos-list">
      
      </ul>
      <div class="filters">
          <div class="dropdown">
              <button class="dropbtn">Filter</button>
              <div class="dropdown-content">
                  <a href="#" id="all">All</a>
                  <a href="#" id="rem">Pending</a>
                  <a href="#" id="com">Completed</a>
              </div>
          </div>
      </div>
      </div>
            <button class="buttonwidhth1" data-modal-target="#modal">Calendar</button>
            <div class="modal" id="modal">
              <div class="modal-header">
                <div class="title">Calendar</div>
                <button data-close-button class="close-button">&times;</button>
              </div>
              <div class="modal-body">
                  <div class="quick-draft p-20 bg-white rad-10">
                      <h2 class="mt-0 mb-10">Calendar</h2>
                      <p class="mt-0 mb-20 c-grey fs-15">Write A Quick Note,Ideas,Events...</p>
                      <div class="containern">
                        <div class="left">
                          <div class="calendar">
                            <div class="month">
                              <i class="fas fa-angle-left prev"></i>
                              <div class="date">december 2015</div>
                              <i class="fas fa-angle-right next"></i>
                            </div>
                            <div class="weekdays">
                              <div>Sun</div>
                              <div>Mon</div>
                              <div>Tue</div>
                              <div>Wed</div>
                              <div>Thu</div>
                              <div>Fri</div>
                              <div>Sat</div>
                            </div>
                            <div class="days"></div>
                            <div class="goto-today">
                              <div class="goto">
                                <input type="text" placeholder="mm/yyyy" class="date-input" />
                                <button class="goto-btn">Go</button>
                              </div>
                              <button class="today-btn">Today</button>
                            </div>
                          </div>
                        </div>
                        <div class="right">
                          <div class="today-date">
                            <div class="event-day">wed</div>
                            <div class="event-date">12th december 2022</div>
                          </div>
                          <div class="events"></div>
                          <div class="add-event-wrapper">
                            <div class="add-event-header">
                              <div class="title">Add Event</div>
                              <i class="fas fa-times close"></i>
                            </div>
                            <div class="add-event-body">
                              <div class="add-event-input">
                                <input type="text" placeholder="Event Name" class="event-name" />
                              </div>
                              <div class="add-event-input">
                                <input
                                  type="text"
                                  placeholder="Event Time From"
                                  class="event-time-from"
                                />
                              </div>
                              <div class="add-event-input">
                                <input
                                  type="text"
                                  placeholder="Event Time To"
                                  class="event-time-to"
                                />
                              </div>
                            </div>
                            <div class="add-event-footer">
                              <button class="add-event-btn">Add Event</button>
                            </div>
                          </div>
                        </div>
                        <button class="add-event">
                          <i class="fas fa-plus"></i>
                        </button>
                      </div>
                  
                      <div class="credits">
        
                      </div>
                    </div>

                    
                    </div>
              </div>
            </div>
            <div id="overlay"></div>

            
          </div>
          </div>
  ,closeModalButtons.forEach(button => {
    button.addEventListener('click', () => {
      const modal = document.querySelector(button.dataset.modalTarget)
      openModal(modal)
    })
  })
  
  ,overlay.addEventListener('click', () => {
    modals.forEach(modal => {
      closeModal(modal)
    })
  }
  
  ,closeModalButtons.forEach(button => {
    button.addEventListener('click', () => {
      closeModal(modal)
    })
  })
  
  ,function openModal(modal) {
    if (modal == null) return
    modal.classList.add('active')
    overlay.classList.add('active')
  }
  
  ,function closeModal(modal) {
    if (modal == null) return
    modal.classList.remove('active')
    overlay.classList.remove('active')
  }
  // const eventsArr = [
  //   {
  //     day: 13,
  //     month: 11,
  //     year: 2022,
  //     events: [
  //       {
  //         title: "Event 1 lorem ipsun dolar sit genfa tersd dsad ",
  //         time: "10:00 AM",
  //       },
  //       {
  //         title: "Event 2",
  //         time: "11:00 AM",
  //       },
  //     ],
  //   },
  // ];
  
  ,getEvents()
  //function to add days in days with class day and prev-date next-date on previous month and next month days and active on today
  ,function initCalendar() {
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const prevLastDay = new Date(year, month, 0);
    const prevDays = prevLastDay.getDate();
    const lastDate = lastDay.getDate();
    const day = firstDay.getDay();
    const nextDays = 7 - lastDay.getDay() - 1;
  
    date.innerHTML = months[month] + " " + year;
  
    let days = "";
  
    for (let x = day; x > 0; x--) {
      days += `<div class="day prev-date">${prevDays - x + 1}</div>`;
    }
  
    for (let i = 1; i <= lastDate; i++) {
      //check if event is present on that day
      let event = false;
      eventsArr.forEach((eventObj) => {
        if (
          eventObj.day === i &&
          eventObj.month === month + 1 &&
          eventObj.year === year
        ) {
          event = true;
        }
      });
      if (
        i === new Date().getDate() &&
        year === new Date().getFullYear() &&
        month === new Date().getMonth()
      ) {
        activeDay = i;
        getActiveDay(i);
        updateEvents(i);
        if (event) {
          days += `<div class="day today active event">${i}</div>`;
        } else {
          days += `<div class="day today active">${i}</div>`;
        }
      } else {
        if (event) {
          days += `<div class="day event">${i}</div>`;
        } else {
          days += `<div class="day ">${i}</div>`;
        }
      }
    }
  
    for (let j = 1; j <= nextDays; j++) {
      days += `<div class="day next-date">${j}</div>`;
    }
    daysContainer.innerHTML = days;
    addListner();
  }
  
  //function to add month and year on prev and next button
  ,function prevMonth() {
    month--;
    if (month < 0) {
      month = 11;
      year--;
    }
    initCalendar();
  }
  
  ,function nextMonth() {
    month++;
    if (month > 11) {
      month = 0;
      year++;
    }
    initCalendar();
  }
  
  ,prev.addEventListener("click", prevMonth)
  ,next.addEventListener("click", nextMonth)
  
  ,initcalendar()
  ,function addListner() {
    const days = document.querySelectorAll(".day");
    days.forEach((day) => {
      day.addEventListener("click", (e) => {
        getActiveDay(e.target.innerHTML);
        updateEvents(Number(e.target.innerHTML));
        activeDay = Number(e.target.innerHTML);
        //remove active
        days.forEach((day) => {
          day.classList.remove("active");
        });
        //if clicked prev-date or next-date switch to that month
        if (e.target.classList.contains("prev-date")) {
          prevMonth();
          //add active to clicked day afte month is change
          setTimeout(() => {
            //add active where no prev-date or next-date
            const days = document.querySelectorAll(".day");
            days.forEach((day) => {
              if (
                !day.classList.contains("prev-date") &&
                day.innerHTML === e.target.innerHTML
              ) {
                day.classList.add("active");
              }
            });
          }, 100);
        } else if (e.target.classList.contains("next-date")) {
          nextMonth();
          //add active to clicked day afte month is changed
          setTimeout(() => {
            const days = document.querySelectorAll(".day");
            days.forEach((day) => {
              if (
                !day.classList.contains("next-date") &&
                day.innerHTML === e.target.innerHTML
              ) {
                day.classList.add("active");
              }
            });
          }, 100);
        } else {
          e.target.classList.add("active");
        }
      });
    });
  }
  
  ,todayBtn.addEventListener("click", () => {
    today = new Date();
    month = today.getMonth();
    year = today.getFullYear();
    calendar();
  })
  
  ,dateInput.addEventListener("input", (e) => {
    dateInput.value = dateInput.value.replace(/[^0-9/]/g, "");
    if (dateInput.value.length === 2) {
      dateInput.value += "/";
    }
    if (dateInput.value.length > 7) {
      dateInput.value = dateInput.value.slice(0, 7);
    }
    if (e.inputType === "deleteContentBackward") {
      if (dateInput.value.length === 3) {
        dateInput.value = dateInput.value.slice(0, 2);
      }
    }
  })
  
  ,gotoBtn.addEventListener("click", gotoDate)
  
  ,function gotoDate() {
    console.log("here");
    const dateArr = dateInput.value.split("/");
    if (dateArr.length === 2) {
      if (dateArr[0] > 0 && dateArr[0] < 13 && dateArr[1].length === 4) {
        month = dateArr[0] - 1;
        year = dateArr[1];
        initCalendar();
        return;
      }
    }
    alert("Invalid Date");
  }
  
  //function get active day day name and date and update eventday eventdate
  ,function getActiveDay(date) {
    const day = new Date(year, month, date);
    const dayName = day.toString().split(" ")[0];
    eventDay.innerHTML = dayName;
    eventDate.innerHTML = date + " " + months[month] + " " + year;
  }
  
  //function update events when a day is active
  ,function updateEvents(date) {
    let events = "";
    eventsArr.forEach((event) => {
      if (
        date === event.day &&
        month + 1 === event.month &&
        year === event.year
      ) {
        event.events.forEach((event) => {
          events += `<div class="event">
              <div class="title">
                <i class="fas fa-circle"></i>
                <h3 class="event-title">${event.title}</h3>
              </div>
              <div class="event-time">
                <span class="event-time">${event.time}</span>
              </div>
          </div>`;
        });
      }
    });
    if (events === "") {
      events = `<div class="no-event">
              <h3>No Events</h3>
          </div>`;
    }
    eventsContainer.innerHTML = events;
    saveEvents();
  }
  
  //function to add event
  ,addEventBtn.addEventListener("click", () => {
    addEventWrapper.classList.toggle("active");
  })
  
  ,addEventCloseBtn.addEventListener("click", () => {
    addEventWrapper.classList.remove("active");
  })
  
  ,document.addEventListener("click", (e) => {
    if (e.target !== addEventBtn && !addEventWrapper.contains(e.target)) {
      addEventWrapper.classList.remove("active");
    }
  })
  
  //allow 50 chars in eventtitle
 , addEventTitle.addEventListener("input", (e) => {
    addEventTitle.value = addEventTitle.value.slice(0, 60);
  })
  
  
  
  //allow only time in eventtime from and to
  ,addEventFrom.addEventListener("input", (e) => {
    addEventFrom.value = addEventFrom.value.replace(/[^0-9:]/g, "");
    if (addEventFrom.value.length === 2) {
      addEventFrom.value += ":";
    }
    if (addEventFrom.value.length > 5) {
      addEventFrom.value = addEventFrom.value.slice(0, 5);
    }
  })
  
  ,addEventTo.addEventListener("input", (e) => {
    addEventTo.value = addEventTo.value.replace(/[^0-9:]/g, "");
    if (addEventTo.value.length === 2) {
      addEventTo.value += ":";
    }
    if (addEventTo.value.length > 5) {
      addEventTo.value = addEventTo.value.slice(0, 5);
    }
  })
  
  //function to add event to eventsArr
  ,addEventSubmit.addEventListener("click", () => {
    const eventTitle = addEventTitle.value;
    const eventTimeFrom = addEventFrom.value;
    const eventTimeTo = addEventTo.value;
    if (eventTitle === "" || eventTimeFrom === "" || eventTimeTo === "") {
      alert("Please fill all the fields");
      return;
    }
  
    //check correct time format 24 hour
    const timeFromArr = eventTimeFrom.split(":");
    const timeToArr = eventTimeTo.split(":");
    if (
      timeFromArr.length !== 2 ||
      timeToArr.length !== 2 ||
      timeFromArr[0] > 23 ||
      timeFromArr[1] > 59 ||
      timeToArr[0] > 23 ||
      timeToArr[1] > 59
    ) {
      alert("Invalid Time Format");
      return;
    }
  
    const timeFrom = convertTime(eventTimeFrom);
    const timeTo = convertTime(eventTimeTo);
  
    //check if event is already added
    let eventExist = false;
    eventsArr.forEach((event) => {
      if (
        event.day === activeDay &&
        event.month === month + 1 &&
        event.year === year
      ) {
        event.events.forEach((event) => {
          if (event.title === eventTitle) {
            eventExist = true;
          }
        });
      }
    });
    if (eventExist) {
      alert("Event already added");
      return;
    }
    const newEvent = {
      title: eventTitle,
      time: timeFrom + " - " + timeTo,
    };
    console.log(newEvent);
    console.log(activeDay);
    let eventAdded = false;
    if (eventsArr.length > 0) {
      eventsArr.forEach((item) => {
        if (
          item.day === activeDay &&
          item.month === month + 1 &&
          item.year === year
        ) {
          item.events.push(newEvent);
          eventAdded = true;
        }
      });
    }
  
    if (!eventAdded) {
      eventsArr.push({
        day: activeDay,
        month: month + 1,
        year: year,
        events: [newEvent],
      });
    }
  
    console.log(eventsArr);
    addEventWrapper.classList.remove("active");
    addEventTitle.value = "";
    addEventFrom.value = "";
    addEventTo.value = "";
    updateEvents(activeDay);
    //select active day and add event class if not added
    const activeDayEl = document.querySelector(".day.active");
    if (!activeDayEl.classList.contains("event")) {
      activeDayEl.classList.add("event");
    }
  })
  
  //function to delete event when clicked on event
  ,eventsContainer.addEventListener("click", (e) => {
    if (e.target.classList.contains("event")) {
      if (confirm("Are you sure you want to delete this event?")) {
        const eventTitle = e.target.children[0].children[1].innerHTML;
        eventsArr.forEach((event) => {
          if (
            event.day === activeDay &&
            event.month === month + 1 &&
            event.year === year
          ) {
            event.events.forEach((item, index) => {
              if (item.title === eventTitle) {
                event.events.splice(index, 1);
              }
            });
            //if no events left in a day then remove that day from eventsArr
            if (event.events.length === 0) {
              eventsArr.splice(eventsArr.indexOf(event), 1);
              //remove event class from day
              const activeDayEl = document.querySelector(".day.active");
              if (activeDayEl.classList.contains("event")) {
                activeDayEl.classList.remove("event");
              }
            }
          }
        });
        updateEvents(activeDay);
      }
    }
  })
  
  //function to save events in local storage
  ,function saveEvents() {
    localStorage.setItem("events", JSON.stringify(eventsArr));
  }
  
  //function to get events from local storage
  ,function getEvents() {
    //check if events are already saved in local storage then return event else nothing
    if (localStorage.getItem("events") === null) {
      return;
    }
    eventsArr.push(...JSON.parse(localStorage.getItem("events")));
  }
  
  ,function convertTime(time) {
    //convert time to 24 hour format
    let timeArr = time.split(":");
    let timeHour = timeArr[0];
    let timeMin = timeArr[1];
    let timeFormat = timeHour >= 12 ? "PM" : "AM";
    timeHour = timeHour % 12 || 12;
    time = timeHour + ":" + timeMin + " " + timeFormat;
    return time;
  }
  //event listners for add and delete
  ,addButton.addEventListener("click", add)
  ,deleteAllButton.addEventListener("click", deleteAll)
  ,deleteSButton.addEventListener("click", deleteS)
  
  
  //event listeners for filtersk
 ,document.addEventListener('click', (e) => {
      if (e.target.className.split(' ')[0] == 'complete' || e.target.className.split(' ')[0] == 'ci') {
          completeTodo(e);
      }
      if (e.target.className.split(' ')[0] == 'delete' || e.target.className.split(' ')[0] == 'di') {
          deleteTodo(e)
      }
      if (e.target.id == "all") {
          viewAll();
      }
      if (e.target.id == "rem") {
          viewRemaining();
      }
      if (e.target.id == "com") {
          viewCompleted();
      }
  
  })
  //event listner for enter key
  ,todoInput.addEventListener('keypress', (e) => {
      if (e.key === 'Enter') {
          add();
      }
  })
  
  
  //updates the all the remaining, completed and main list
  ,function update() {
      comdoList = todoList.filter((ele) => {
          return ele.complete
  
      })
      remList = todoList.filter((ele) => {
          return !ele.complete
      })
      document.getElementById("r-count").innerText = todoList.length.toString();
      document.getElementById("c-count").innerText = comdoList.length.toString();
  
  }
  
  //adds the task in main list
  
  ,function add() {
      var value = todoInput.value;
      if (value === '') {
          alert("😮 Task cannot be empty")
          return;
      }
      todoList.push({
          task: value,
          id: Date.now().toString(),
          complete: false,
      });
  
      todoInput.value = "";
      update();
      addinmain(todoList);
  }
  
  
  //renders the main list and views on the main content
  
  ,function addinmain(todoList) {
      allTodos.innerHTML = ""
      todoList.forEach(element => {
          var x = `<li id=${element.id} class="todo-item">
      <p id="task"> ${element.complete ? `<strike>${element.task}</strike>` : element.task} </p>
      <div class="todo-actions">
                  <button class="complete btn btn-success">
                      DONE
                  </button>
  
                  <button class="delete btn btn-error" >
                      DEL
                  </button>
              </div>
          </li>`
          allTodos.innerHTML += x
      });
  }
  
  
  //deletes and indiviual task and update all the list
  ,function deleteTodo(e) {
      var deleted = e.target.parentElement.parentElement.getAttribute('id');
      todoList = todoList.filter((ele) => {
          return ele.id != deleted
      })
  
      update();
      addinmain(todoList);
  
  }
  
  //completes indiviaula task and updates all the list
  ,function completeTodo(e) {
      var completed = e.target.parentElement.parentElement.getAttribute('id');
      todoList.forEach((obj) => {
          if (obj.id == completed) {
              if (obj.complete == false) {
                  obj.complete = true
                  e.target.parentElement.parentElement.querySelector("#task").classList.add("line");
              } else {
                  obj.complete = false
  
                  e.target.parentElement.parentElement.querySelector("#task").classList.remove("line");
              }
          }
      })
  
      update();
      addinmain(todoList);
  }
  
  
  //deletes all the tasks
  ,function deleteAll(todo) {
  
      todoList = []
  
      update();
      addinmain(todoList);
  
  }
  
  //deletes only completed task
  ,function deleteS(todo) {
  
      todoList = todoList.filter((ele) => {
          return !ele.complete;
      })
  
  
      update();
      addinmain(todoList);
  
  }
  
  
  // functions for filters
  ,function viewCompleted() {
      addinmain(comdoList);
  }
  
  ,function viewRemaining() {
  
      addinmain(remList);
  }
  ,function viewAll() {
      addinmain(todoList);
    }
  ))
};
