import React, { useState } from "react";
import Calendar from "react-calendar"; // Assurez-vous d'importer 'react-calendar'
import "react-calendar/dist/Calendar.css"; // Assurez-vous que les styles sont inclus

export default function Dashboard() {
  const [date, setDate] = useState(new Date()); // Placez l'état à l'intérieur de la fonction

  return (
    <div style={{ padding: "20px" }}>
      <h1>React Calendar Example</h1>
      <Calendar onChange={setDate} value={date} />
      <p>Selected date: {date.toDateString()}</p>
    </div>
  );
}
