import React, { useState } from 'react';
import './index.scss';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [habits, setHabits] = useState([]);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleSubmit = () => {
    if (inputValue.trim()) {
      // Ajouter l'habitude à la liste
      setHabits((prevHabits) => [
        ...prevHabits,
        { text: inputValue, id: Date.now(), animated: false }
      ]);
      setInputValue('');
    } else {
      alert("Please enter a habit before submitting!");
    }
  };

  return (
    <div className="categories-container">
      {/* Bloc d'ajout dynamique */}
      <div className="new-habit-container">
        <h2>Add new bad habit</h2>
        <input 
          type="text" 
          placeholder="Type something..." 
          value={inputValue}
          onChange={handleInputChange} 
        />
        <button onClick={handleSubmit}>Submit</button>
      </div>

      {/* Affichage des habitudes ajoutées avec animation */}
      {habits.map((habit) => (
        <div
          key={habit.id}
          className={`category-container ${habit.animated ? 'translated' : ''}`}
          onAnimationEnd={() => {
            // Marquer l'animation comme terminée
            setHabits((prevHabits) =>
              prevHabits.map((h) =>
                h.id === habit.id ? { ...h, animated: true } : h
              )
            );
          }}
        >
          <div className="category-body-container">
            <h2>{habit.text}</h2>
          </div>
        </div>
      ))}
    </div>
  );
};

export default App;
