import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { doc, getDoc } from "firebase/firestore";
import { useAuth } from "../context/AuthContext";
import { db } from "../fireba/firebase";
import photo from "./profile-user.png";
import change from "./cigarette.png";
import setting from "./settings.png";
import multipe from "./multiple-users-silhouette.png";
import friend from "./friends.png";
function Setting() {
  const { currentUser, updateUserPassword } = useAuth();
  const [userData, setUserData] = useState({ name: "", age: "", country: "" });
  const [password, setPassword] = useState("");
  const [passwordConfirm, setPasswordConfirm] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    async function loadUserData() {
      if (currentUser) {
        const data = await fetchUserData(currentUser.uid);
        if (data) {
          setUserData(data);
        }
      }
    }
    loadUserData();
  }, [currentUser]);

  async function fetchUserData(userId) {
    const docRef = doc(db, "users", userId);
    const docSnap = await getDoc(docRef);

    if (docSnap.exists()) {
      return docSnap.data();
    } else {
      console.error("No such document!");
      return null;
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    if (passwordRef.current.value !== passwordConfirmRef.current.value) {
      return setError("Passwords do not match");
    }

    const promises = [];
    setLoading(true);
    setError("");

    if (emailRef.current.value !== currentUser.email) {
      promises.push(updateUserEmail(emailRef.current.value));
    }
    if (passwordRef.current.value) {
      promises.push(updateUserPassword(passwordRef.current.value));
    }

    Promise.all(promises)
      .then(() => {
        navigate("/");
      })
      .catch(() => {
        setError("Failed to update account");
      })
      .finally(() => {
        setLoading(false);
      });
  }
  function handleInputChange(e) {
    const { name, value } = e.target;
    setUserData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  }

  return (
    <div className="page d-flex">
      <div className="sidebar bg-white p-20 p-relative">
        <h3 className="p-relative txt-c mt-0">habit-up</h3>
        <ul>
          <li>
            <Link
              to="/dashboard"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={photo} style={{ width: "24px" }} alt="Dashboard" />
              <span>Dashboard</span>
            </Link>
          </li>
          <li>
            <Link
              to="/setting"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={setting} style={{ width: "24px" }} alt="Setting" />
              <span>Setting</span>
            </Link>
          </li>
          <li>
            <Link
              to="/yourhabits"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={change} style={{ width: "24px" }} alt="Habits" />
              <span>Habits</span>
            </Link>
          </li>
          <li>
            <Link
              to="/challenges"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={multipe} style={{ width: "24px" }} alt="Challenges" />
              <span>Challenges</span>
            </Link>
          </li>
          <li>
            <Link
              to="/friends"
              className="d-flex align-center fs-14 c-black rad-6 p-10"
            >
              <img src={friend} style={{ width: "24px" }} alt="Friends" />
              <span>Friends</span>
            </Link>
          </li>
        </ul>
      </div>
      <div className="content w-full">
        <h1 className="p-relative">Settings</h1>
        <div className="settings-page m-20 d-grid gap-20">
          {/* General Info Section */}
          <div className="p-20 bg-white rad-10">
            <h2 className="mt-0 mb-10">General Info</h2>
            <p className="mt-0 mb-20 c-grey fs-15">
              General Information About Your Account
            </p>
            <div className="mb-15">
              <label className="fs-14 c-grey d-block mb-10" htmlFor="name">
                Name
              </label>
              <input
                className="b-none border-ccc p-10 rad-6 d-block w-full"
                type="text"
                id="name"
                name="name"
                placeholder="Name"
                value={userData.name}
                onChange={handleInputChange}
              />
            </div>
            <div className="mb-15">
              <label className="fs-14 c-grey d-block mb-10" htmlFor="email">
                Email
              </label>
              <input
                type="email"
                value={currentUser && currentUser.email}
                readOnly
              />
            </div>
          </div>
          {/* Security Info Section */}
          <div className="p-20 bg-white rad-10">
            <h2 className="mt-0 mb-10">Security Info</h2>
            <p className="mt-0 mb-20 c-grey fs-15">
              Security Information About Your Account
            </p>
            {error && <p className="error">{error}</p>}
            <form onSubmit={handlePasswordChange}>
              <div className="mb-15">
                <label className="fs-14 c-grey d-block mb-10" htmlFor="password">
                  New Password
                </label>
                <input
                  type="password"
                  id="password"
                  className="b-none border-ccc p-10 rad-6 d-block w-full"
                  placeholder="Enter new password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>
              <div className="mb-15">
                <label
                  className="fs-14 c-grey d-block mb-10"
                  htmlFor="passwordConfirm"
                >
                  Confirm Password
                </label>
                <input
                  type="password"
                  id="passwordConfirm"
                  className="b-none border-ccc p-10 rad-6 d-block w-full"
                  placeholder="Confirm new password"
                  value={passwordConfirm}
                  onChange={(e) => setPasswordConfirm(e.target.value)}
                />
              </div>
              <button
                type="submit"
                className="button bg-blue c-white btn-shape"
                disabled={loading}
              >
                {loading ? "Updating..." : "Update Password"}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Setting;
