export default function calendrier(){
const openModalButtons = document.querySelectorAll('[data-modal-target]')
const closeModalButtons = document.querySelectorAll('[data-close-button]')
const overlay = document.getElementById('overlay')
openModalButtons.forEach(button => {
  button.addEventListener('click', () => {
    const modal = document.querySelector(button.dataset.modalTarget)
    openModal(modal)
  })
})

overlay.addEventListener('click', () => {
  const modals = document.querySelectorAll('.modal.active')
  modals.forEach(modal => {
    closeModal(modal)
  })
})

closeModalButtons.forEach(button => {
  button.addEventListener('click', () => {
    const modal = button.closest('.modal')
    closeModal(modal)
  })
})

function openModal(modal) {
  if (modal == null) return
  modal.classList.add('active')
  overlay.classList.add('active')
}

function closeModal(modal) {
  if (modal == null) return
  modal.classList.remove('active')
  overlay.classList.remove('active')
}
return(
    <div class="quick-draft p-20 bg-white rad-10">
    <h2 className="a">Monthly Targets List</h2>
<div class="input-section">
  <input id="todo-input" type="text" placeholder="ðŸ“‘ Add your new monthly target here.."
      class="input w-full max-w-xs" />
  <button id="add-button" class="btn btn-add " >
      ADD
  </button>
</div>
<div class="todos-filter">
          <button id="delete-selected" class="btn btn-ghost delete-selected-btn">
              Delete Selected
          </button>
          <button id="delete-all" class="btn btn-ghost delete-all-btn">
              Delete All
          </button>
          </div>
          <ul id="all-todos" class="todos-list">
</ul>
<div class="filters">
<button class="dropbtn">Filter</button>
              <div class="dropdown-content">
                  <a href="#" id="all">All</a>
                  <a href="#" id="rem">Pending</a>
                  <a href="#" id="com">Completed</a>
              </div>
              <div class="completed-task">
              <p>Completed: <span id="c-count">0</span></p>
          </div>
          <div class="remaining-task">
              <p>Total Targets: <span id="r-count">0</span></p>
          </div>
    </div>
    </div>
);
};
