import React, { useState } from 'react';
import './index.scss';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [displayedValue, setDisplayedValue] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false); // Etat pour savoir si le formulaire a été soumis
  const [error, setError] = useState(''); // Etat pour gérer les erreurs

  // Fonction de gestion de la saisie
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
    setError(''); // Réinitialiser l'erreur lorsque l'utilisateur tape quelque chose
  };

  // Fonction pour afficher l'entrée et cacher le formulaire
  const handleDisplayInput = () => {
    if (inputValue.trim() === '') {
      setError('Please enter a habit before submitting!');
    } else {
      setDisplayedValue(inputValue);
      setInputValue(''); // Effacer la valeur de l'input
      setIsSubmitted(true); 
      setError('');
    }
  };

  return (
    <div className='categories-container'>
      <div className='category-container'>
        <div className='category-body-container'>
          {!isSubmitted && <h2>Add new bad Habit</h2>}
          {!isSubmitted ? (
            <>
              <input 
                type="text" 
                placeholder="Type something..." 
                value={inputValue}
                onChange={handleInputChange} 
              />
              <button onClick={handleDisplayInput}>Submit</button>
              {error && <p style={{ color: 'black' }}>{error}</p>}
            </>
          ) : (
            <p>{displayedValue}</p>
          )}
          </div>
        </div>
      </div>
    );
  };
  
  export default App;
