import { useEffect } from 'react';

export const  Calendrier=()=> {
  useEffect(() => {
    const openModalButtons = document.querySelectorAll('[data-modal-target]');
    const closeModalButtons = document.querySelectorAll('[data-close-button]');
    const overlay = document.getElementById('overlay');

    // Open modal logic
    openModalButtons.forEach(button => {
      button.addEventListener('click', () => {
        const modal = document.querySelector(button.dataset.modalTarget);
        openModal(modal);
      });
    });

    // Close modal when overlay is clicked
    if (overlay) {
      overlay.addEventListener('click', () => {
        const modals = document.querySelectorAll('.modal.active');
        modals.forEach(modal => closeModal(modal));
      });
    }

    // Close modal buttons logic
    closeModalButtons.forEach(button => {
      button.addEventListener('click', () => {
        const modal = button.closest('.modal');
        closeModal(modal);
      });
    });

    // Clean up event listeners on unmount
    return () => {
      openModalButtons.forEach(button => {
        button.removeEventListener('click', () => {
          const modal = document.querySelector(button.dataset.modalTarget);
          openModal(modal);
        });
      });

      if (overlay) {
        overlay.removeEventListener('click', () => {
          const modals = document.querySelectorAll('.modal.active');
          modals.forEach(modal => closeModal(modal));
        });
      }

      closeModalButtons.forEach(button => {
        button.removeEventListener('click', () => {
          const modal = button.closest('.modal');
          closeModal(modal);
        });
      });
    };
  }, []); // Dependency array ensures this runs after the component mounts

  // Helper functions
  function openModal(modal) {
    if (modal == null) return;
    modal.classList.add('active');
    const overlay = document.getElementById('overlay');
    if (overlay) overlay.classList.add('active');
  }

  function closeModal(modal) {
    if (modal == null) return;
    modal.classList.remove('active');
    const overlay = document.getElementById('overlay');
    if (overlay) overlay.classList.remove('active');
  }
  return(
    <div className="quick-draft p-20 bg-white rad-10">
      <h2 className="a">Monthly Targets List</h2>
      <div className="input-section">
        <input
          id="todo-input"
          type="text"
          placeholder="ðŸ“‘ Add your new monthly target here.."
          className="input w-full max-w-xs"
        />
        <button id="add-button" className="btn btn-add">
          ADD
        </button>
      </div>
      <div className="todos-filter">
        <button id="delete-selected" className="btn btn-ghost delete-selected-btn">
          Delete Selected
        </button>
        <button id="delete-all" className="btn btn-ghost delete-all-btn">
          Delete All
        </button>
      </div>
      <ul id="all-todos" className="todos-list"></ul>
      <div className="filters">
        <button className="dropbtn">Filter</button>
        <div className="dropdown-content">
          <a href="#" id="all">
            All
          </a>
          <a href="#" id="rem">
            Pending
          </a>
          <a href="#" id="com">
            Completed
          </a>
        </div>
        <div className="completed-task">
          <p>
            Completed: <span id="c-count">0</span>
          </p>
        </div>
        <div className="remaining-task">
          <p>
            Total Targets: <span id="r-count">0</span>
          </p>
        </div>
      </div>
    </div>);
};
