import React, { useState } from 'react';
import './index.scss';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [displayedValue, setDisplayedValue] = useState('');

  // Fonction de gestion de la saisie
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  // Fonction pour afficher l'entrée et effacer l'ancienne
  const handleDisplayInput = () => {
    setDisplayedValue(inputValue);
    setInputValue(''); // Efface le champ de saisie
  };

  return (
    <div className='categories-container'>
      <div className='category-container'>
        {/* <img /> */}
        <div className='category-body-container'>
          <h2>Add new bad Habit</h2>
          <input 
            type="text" 
            placeholder="Type something..." 
            value={inputValue}
            onChange={handleInputChange} 
          />
          <button onClick={handleDisplayInput}>Submit</button>
          {/* Afficher seulement la dernière saisie */}
          {displayedValue && <p>Output: {displayedValue}</p>}
        </div>
      </div>
    </div>
  );
};

export default App;
