import React, { useState } from "react";
import { database, ref, push, set } from "../fireba/firebase";
import "../h/home.css";


const Contact = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [loader, setLoader] = useState(false);
  const [alert, setAlert] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);

    // Save message to Firebase
    const messagesRef = ref(database, 'messages');
    const newMessageRef = push(messagesRef);
    set(newMessageRef, {
      name: name,
      email: email,
      message: message,
    })
      .then(() => {
        setLoader(false);
        setAlert(true);

        // Hide alert after 3 seconds
        setTimeout(() => {
          setAlert(false);
        }, 3000);

        // Clear form
        setName("");
        setEmail("");
        setMessage("");
      })
      .catch((error) => {
        alert(error.message);
        setLoader(false);
      });
  };

  return (
    <div className="homecontact">
    <div className="homecontainer">
      <h2 className="homespecial-heading">Contact Us</h2>
      <p>Have any questions or need help? Feel free to reach out to us.</p>
      <div className="homecontact-content">
        <div className="homecontact-info">
      <form id="homecontact-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Your Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <textarea
          placeholder="Your Message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          required
        ></textarea>
        <button
          type="submit"
          className="btn-primary"
          style={{ background: loader ? "#ccc" : "blue" }}
          disabled={loader}
        >
          {loader ? "Submitting..." : "Send Message"}
        </button>
      </form>
      {alert && <div className="alert">Your message has been sent!</div>}
    </div>
    </div>
        </div>
      </div>
  );
};

export default Contact;
