import React, { useState } from 'react';
import './index.scss';

const App = () => {
  const [inputValue, setInputValue] = useState('');
  const [habits, setHabits] = useState([]);
  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };
  const handleAddHabit = () => {
    if (inputValue.trim() === '') {
      alert('Please enter a habit before submitting!');
    } else {
      setHabits([...habits, inputValue]);
      setInputValue('');
    }
  };
  return (
    
    <div className='categories-container'>
    <div class="w3-top">
    <div class="w3-bar w3-red w3-card w3-left-align w3-large">
    <a></a>
    <a href="#">Home</a>
    <a href="#">Link 1</a>
    <a href="#">Link 2</a>
    <a href="#">Link 3</a>
    <a href="#">Link 4</a>
  </div>
  <div id="navDemo">
    <a href="#">Link 1</a>
    <a href="#">Link 2</a>
    <a href="#">Link 3</a>
    <a href="#">Link 4</a>
  </div>
</div>
<header>
  <h1 >START PAGE</h1>
  <p>Template by w3.css</p>
  <button className='K'>Get Started</button>
</header>
      <div className='category-container'>
        <div className='category-body-container'>
          <h2>Add a New Habit</h2>
          <input className='i'
            type="text" 
            placeholder="Type a new habit..." 
            value={inputValue}
            onChange={handleInputChange} 
          />
          <button className='g' onClick={handleAddHabit}>Add Habit</button>
          <div className="habits-grid">
            {habits.map((habit, index) => (
              <div key={index} className="habit-card">
                <p>{habit}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
