import React from "react";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import "./style.css";
import "./normalize.css";
import "./all.min.css";

const Header = () => (
  <div className="mainContainer">
    <div className="mainBody">
      <img className="logo" src="./login&register/images/habit-tracker-gif-looponce.gif" alt="Girl in a jacket" />
      <h1>Welcome Back.</h1>
      <form>
        <label htmlFor="username">Username:</label>
        <input type="text" id="username" name="username" required />
        <label htmlFor="password">Password:</label>
        <p>
          <input type="password" name="password" id="password" required />
          <i className="far fa-eye" id="togglePassword" style={{ marginLeft: "-30px", cursor: "pointer" }}></i>
        </p>
        <input type="submit" id="loginButton" name="loginButton" value="Login" required />
      </form>
      <p className="link">Don't have an account? <Link to="/register">Register here</Link></p> {/* Link updated */}
    </div>
  </div>
);

const Login = () => (
  <>
    <Header />
  </>
);

export default Login;
